package task

type aarray struct {
	count int
	name  string
	en    []string
	tj    []string
	ru    []string
}

func NewArrayTask() Task {
	a := &aarray{
		count: 141,
		name:  "Array",
	}
	a.makeEn()
	a.makeTj()
	a.makeRu()
	return a
}

func (a *aarray) Count() int   { return a.count }
func (a *aarray) Name() string { return a.name }
func (a *aarray) En() []string { return a.en }
func (a *aarray) Tj() []string { return a.tj }
func (a *aarray) Ru() []string { return a.ru }

func (a *aarray) makeEn() {
	a.en = make([]string, a.count)
	a.en[1] = `Given an integer&nbsp;<i>N</i> (&gt;&nbsp;0), create and output an array of <i>N</i>&nbsp;integers that are the first positive odd numbers: 1,&nbsp;3, 5,&nbsp;&#8230;&nbsp;.`
	a.en[2] = `Given an integer&nbsp;<i>N</i> (&gt;&nbsp;0), create and output an array of <i>N</i>&nbsp;integers that are the first positive integer powers of 2: 2,&nbsp;4, 8,&nbsp;16,&nbsp;&#8230;&nbsp;.`
	a.en[3] = `An integer&nbsp;<i>N</i> (&gt;&nbsp;1), the first term&nbsp;<i>A</i> and the common difference&nbsp;<i>D</i> of an <i>arithmetic sequence</i> are given (<i>A</i> and&nbsp;<i>D</i> are real numbers). Create and output an array of <i>N</i>&nbsp;real numbers that are the initial terms of this sequence: <i>A</i>, &nbsp; &nbsp;<i>A</i>&nbsp;+&nbsp;<i>D</i>, &nbsp; &nbsp;<i>A</i>&nbsp;+&nbsp;2&#183;<i>D</i>, &nbsp; &nbsp;<i>A</i>&nbsp;+&nbsp;3&#183;<i>D</i>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.en[4] = `An integer&nbsp;<i>N</i> (&gt;&nbsp;1), the first term&nbsp;<i>A</i> and the common ratio&nbsp;<i>R</i> of a <i>geometric sequence</i> are given (<i>A</i> and&nbsp;<i>D</i> are real numbers). Create and output an array of <i>N</i>&nbsp;real numbers that are the initial terms of this sequence: <i>A</i>, &nbsp; &nbsp;<i>A</i>&#183;<i>R</i>, &nbsp; &nbsp;<i>A</i>&#183;<i>R</i><sup>2</sup>, &nbsp; &nbsp;<i>A</i>&#183;<i>R</i><sup>3</sup>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.en[5] = `Given an integer&nbsp;<i>N</i> (&gt;&nbsp;2), create and output an array of <i>N</i>&nbsp;integers that are the initial terms of a sequence {<i>F</i><sub><i>K</i></sub>} of the <i>Fibonacci numbers</i>: <i>F</i><sub>1</sub>&nbsp;=&nbsp;1, &nbsp; &nbsp; &nbsp; &nbsp;<i>F</i><sub>2</sub>&nbsp;=&nbsp;1, &nbsp; &nbsp; &nbsp; &nbsp;<i>F</i><sub><i>K</i></sub>&nbsp;=&nbsp;<i>F</i><sub><i>K</i>&#8722;2</sub>&nbsp;+&nbsp;<i>F</i><sub><i>K</i>&#8722;1</sub>, &nbsp; &nbsp;<i>K</i>&nbsp;=&nbsp;3,&nbsp;4,&nbsp;&#8230;&nbsp;.`
	a.en[6] = `Given three integers&nbsp;<i>N</i> (&gt;&nbsp;2), <i>A</i>, <i>B</i>, create and output an array of <i>N</i>&nbsp;integers such that the first element is equal to&nbsp;<i>A</i>, the second one is equal to&nbsp;<i>B</i>, and each subsequent element is equal to the sum of all previous ones.`
	a.en[7] = `Given an array of <i>N</i>&nbsp;real numbers, output its elements in inverse order.`
	a.en[8] = `Given an array of <i>N</i>&nbsp;integers, output all odd numbers contained in the array in ascending order of their indices. Also output the amount&nbsp;<i>K</i> of odd numbers contained in the array.`
	a.en[9] = `Given an array of <i>N</i>&nbsp;integers, output all even numbers contained in the array in descending order of their indices. Also output the amount&nbsp;<i>K</i> of even numbers contained in the array.`
	a.en[10] = `Given an array of <i>N</i>&nbsp;integers, output all even numbers contained in the array in ascending order of their indices and then output all odd numbers contained in the array in descending order of their indices.`
	a.en[11] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Output array elements with order numbers that are multiples of&nbsp;<i>K</i>: <i>A</i><sub><i>K</i></sub>, <i>A</i><sub>2&#183;<i>K</i></sub>, <i>A</i><sub>3&#183;<i>K</i></sub>,&nbsp;&#8230;&nbsp;. Do not use conditional statements.`
	a.en[12] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given (<i>N</i>&nbsp;is an even number). Output array elements with even order numbers in ascending order of indices: <i>A</i><sub>2</sub>, <i>A</i><sub>4</sub>, <i>A</i><sub>6</sub>,&nbsp;&#8230;, <i>A</i><sub><i>N</i></sub>. Do not use conditional statements.`
	a.en[13] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given (<i>N</i>&nbsp;is an odd number). Output array elements with odd order numbers in descending order of indices: <i>A</i><sub><i>N</i></sub>, <i>A</i><sub><i>N</i>&#8722;2</sub>, <i>A</i><sub><i>N</i>&#8722;4</sub>,&nbsp;&#8230;, <i>A</i><sub>1</sub>. Do not use conditional statements.`
	a.en[14] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Output array elements with even order numbers (in ascending order of indices) and then output array elements with odd order numbers (in ascending order of indices too): <i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub>6</sub>, &nbsp; &nbsp;&#8230;, &nbsp; &nbsp;<i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>5</sub>, &nbsp; &nbsp;&#8230;&nbsp;.Do not use conditional statements.`
	a.en[15] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Output array elements with odd order numbers in ascending order of indices and then output array elements with even order numbers in descending order of indices: <i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>5</sub>, &nbsp; &nbsp;&#8230;, &nbsp; &nbsp;<i>A</i><sub>6</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub>2</sub>.Do not use conditional statements.`
	a.en[16] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Output array elements as follows: <i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i></sub>, &nbsp; &nbsp;<i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;2</sub>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.en[17] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Output array elements as follows: <i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i></sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;2</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;3</sub>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.en[18] = `An array&nbsp;<i>A</i> of 10&nbsp;nonzero integers is given.  Output the value of the first element&nbsp;<i>A</i><sub><i>K</i></sub> that satisfies the following inequality: <i>A</i><sub><i>K</i></sub>&nbsp;&lt;&nbsp;<i>A</i><sub>10</sub>. If the array does not contain such elements then output&nbsp;0.`
	a.en[19] = `An array&nbsp;<i>A</i> of 10&nbsp;integers is given.  Output the order number of the last element&nbsp;<i>A</i><sub><i>K</i></sub> that satisfies the following double inequality: <i>A</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>A</i><sub><i>K</i></sub>&nbsp;&lt;&nbsp;<i>A</i><sub>10</sub>. If the array does not contain such elements then output&nbsp;0.`
	a.en[20] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Find the sum of array elements with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>L</i> inclusively.`
	a.en[21] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Find the average of array elements with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>L</i> inclusively.`
	a.en[22] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&lt;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Find the sum of all array elements except ones with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>L</i> inclusively.`
	a.en[23] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&lt;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Find the average of all array elements except ones with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>L</i> inclusively.`
	a.en[24] = `An array of <i>N</i>&nbsp;distinct integers is given. If the array elements represent an <i>arithmetic sequence</i> (see Array3) then output its common difference, otherwise output&nbsp;0.`
	a.en[25] = `An array of <i>N</i>&nbsp;nonzero integers is given. If the array elements represent a <i>geometric sequence</i> (see Array4) then output its common ratio, otherwise output&nbsp;0.`
	a.en[26] = `An array of <i>N</i>&nbsp;integers is given.  If odd and even numbers are alternated in the array then output&nbsp;0, otherwise output the order number of the first element that breaks the above condition.`
	a.en[27] = `An array of <i>N</i>&nbsp;nonzero integers is given.  If positive and negative numbers are alternated in the array then output&nbsp;0, otherwise output the order number of the first element that breaks the above condition.`
	a.en[28] = `Given an array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers, find the minimum among elements with even order numbers:&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>4</sub>, <i>A</i><sub>6</sub>,&nbsp;&#8230;&nbsp;.`
	a.en[29] = `Given an array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers, find the maximum among elements with odd order numbers:&nbsp;<i>A</i><sub>1</sub>, <i>A</i><sub>3</sub>, <i>A</i><sub>5</sub>,&nbsp;&#8230;&nbsp;.`
	a.en[30] = `An array of <i>N</i>&nbsp;real numbers is given. Find the order numbers of array elements that are greater than their right neighbor. Output these order numbers in ascending order and also output the amount of such elements.`
	a.en[31] = `An array of <i>N</i>&nbsp;real numbers is given. Find the order numbers of array elements that are greater than their left neighbor. Output these order numbers in descending order and also output the amount of such elements.`
	a.en[32] = `Given an array of <i>N</i>&nbsp;real numbers, find the order number of its first local minimum (an array element is called the <i>local minimum</i> if it is smaller than its neighbors).`
	a.en[33] = `Given an array of <i>N</i>&nbsp;real numbers, find the order number of its last local maximum (an array element is called the <i>local maximum</i> if it is greater than its neighbors).`
	a.en[34] = `Given an array of <i>N</i>&nbsp;real numbers, find the maximum among its local minimums (see the <i>local minimum</i> definition in Array32).`
	a.en[35] = `Given an array of <i>N</i>&nbsp;real numbers, find the minimum among its local maximums (see the <i>local maximum</i> definition in Array33).`
	a.en[36] = `Given an array of <i>N</i>&nbsp;real numbers, find the maximum among array elements that are neither local minimum nor local maximum (see the definitions of <i>local minimum</i> and <i>local maximum</i> in Array32 and Array33 respectively). If the array does not contain such elements then output&nbsp;0 (as a real number).`
	a.en[37] = `Given an array of <i>N</i>&nbsp;real numbers, find the amount of its <i>parts of ascending</i> (that is, parts that contain elements whose values are in ascending order).`
	a.en[38] = `Given an array of <i>N</i>&nbsp;real numbers, find the amount of its <i>parts of descending</i> (that is, parts that contain elements whose values are in descending order).`
	a.en[39] = `Given an array of <i>N</i>&nbsp;real numbers, find the amount of its <i>parts of monotonicity</i> (that is, parts that contain elements whose values are in ascending or in descending order).`
	a.en[40] = `A real number&nbsp;<i>R</i> and an array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers are given. Find the array element that is the <i>nearest</i> to the number&nbsp;<i>R</i> (i.&nbsp;e., an element&nbsp;<i>A</i><sub><i>K</i></sub> such that the value |<i>A</i><sub><i>K</i></sub>&nbsp;&#8722;&nbsp;<i>R</i>| is minimal).`
	a.en[41] = `Given an array of <i>N</i>&nbsp;real numbers, find two adjacent elements with the maximal sum of values and output these elements in ascending order of its indices.`
	a.en[42] = `A real number&nbsp;<i>R</i> and an array of <i>N</i>&nbsp;real numbers are given. Find two adjacent elements whose sum of values is the nearest to the number&nbsp;<i>R</i> and output these elements in ascending order of its indices (see the definition of two nearest numbers in Array40).`
	a.en[43] = `Given an array of <i>N</i>&nbsp;integers whose values are in ascending or in descending order, find the amount of its elements with distinct values.`
	a.en[44] = `An array of <i>N</i>&nbsp;integers is given, the array contains two elements with equal values. Find these elements and output their order numbers in ascending order.`
	a.en[45] = `Given an array of <i>N</i>&nbsp;real numbers, find two nearest array elements (i.&nbsp;e., two different elements&nbsp;<i>A</i><sub><i>K</i></sub> and&nbsp;<i>A</i><sub><i>L</i></sub> such that the value |<i>A</i><sub><i>K</i></sub>&nbsp;&#8722;&nbsp;<i>A</i><sub><i>L</i></sub>| is minimal) and output their order numbers in ascending order.`
	a.en[46] = `A real number&nbsp;<i>R</i> and an array of <i>N</i>&nbsp;real numbers are given. Find two different elements such that the sum of their values is the nearest to the number&nbsp;<i>R</i>, and output these elements in ascending order of indices (see the definition of two nearest numbers in Array40).`
	a.en[47] = `Given an array of <i>N</i>&nbsp;integers, find the amount of its elements with distinct values.`
	a.en[48] = `Given an array of <i>N</i>&nbsp;integers, find the maximal amount of its elements with equal values.`
	a.en[49] = `An array of <i>N</i>&nbsp;integers is given. If the array is a <i>permutation</i> (i.&nbsp;e., if the array contains all integers in the range&nbsp;1 to&nbsp;<i>N</i>) then output&nbsp;0, otherwise output the order number of the first inadmissible element.`
	a.en[50] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;integers is given, the array is a permutation (see the <i>permutation</i> definition in Array49). Find the amount of <i>inversions</i> in this permutation (i.&nbsp;e., the amount of pairs of elements&nbsp;<i>A</i><sub><i>I</i></sub> and&nbsp;<i>A</i><sub><i>J</i></sub> such that <i>I</i>&nbsp;&lt;&nbsp;<i>J</i> and <i>A</i><sub><i>I</i></sub>&nbsp;&gt;&nbsp;<i>A</i><sub><i>J</i></sub>).`
	a.en[51] = `Two arrays&nbsp;<i>A</i> and&nbsp;<i>B</i> of <i>N</i>&nbsp;real numbers are given. Exchange the contents of the given arrays and output elements of the changed array&nbsp;<i>A</i> and then output elements of the changed array&nbsp;<i>B</i>.`
	a.en[52] = `Given an array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers, create a new array&nbsp;<i>B</i> of the same size with elements defined as: <table align="center"><tr><td align=right><i>B</i><sub><i>K</i></sub><td align=center>&nbsp;=&nbsp;<td align=right>2&#183;<i>A</i><sub><i>K</i></sub>,<td align=left>if <i>A</i><sub><i>K</i></sub>&nbsp;&lt;&nbsp;5,    <tr><td align=right>&nbsp;<td align=center>&nbsp;<td align=right><i>A</i><sub><i>K</i></sub>/2<td align=left>otherwise.</table></p>`
	a.en[53] = `Two arrays&nbsp;<i>A</i> and&nbsp;<i>B</i> of <i>N</i>&nbsp;real numbers are given. Create a new array&nbsp;<i>C</i> of the same size; each element&nbsp;<i>C</i><sub><i>K</i></sub> must be equal to the largest of elements of&nbsp;<i>A</i> and&nbsp;<i>B</i> with the same index&nbsp;<i>K</i>.`
	a.en[54] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;integers is given. Write elements of&nbsp;<i>A</i> whose values are even numbers to a new array&nbsp;<i>B</i> (in the same order) and output the size of array&nbsp;<i>B</i> and all its elements.`
	a.en[55] = `An array&nbsp;<i>A</i> of&nbsp;<i>N</i> (&#8804;&nbsp;15) integers is given. Write elements of&nbsp;<i>A</i> with odd order numbers (1, 3,&nbsp;&#8230;) to a new array&nbsp;<i>B</i> and output the size of array&nbsp;<i>B</i> and all its elements. Do not use conditional statements.`
	a.en[56] = `An array&nbsp;<i>A</i> of&nbsp;<i>N</i> (&#8804;&nbsp;15) integers is given. Write elements of&nbsp;<i>A</i> with order numbers that are multiples of&nbsp;3 (3, 6,&nbsp;&#8230;) to a new array&nbsp;<i>B</i> and output the size of array&nbsp;<i>B</i> and all its elements. Do not use conditional statements.`
	a.en[57] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;integers is given. Write elements of&nbsp;<i>A</i> with even order numbers to a new array&nbsp;<i>B</i> and then write elements of&nbsp;<i>A</i> with odd order numbers to the same array&nbsp;<i>B</i>, so the array&nbsp;<i>B</i> will contain the following elements: <i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub>6</sub>, &nbsp; &nbsp;&#8230;, &nbsp; &nbsp;<i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>5</sub>, &nbsp; &nbsp;&#8230;&nbsp;.Do not use conditional statements.`
	a.en[58] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Create a new array&nbsp;<i>B</i> of the same size; each array element&nbsp;<i>B</i><sub><i>K</i></sub> must be equal to the sum of elements of&nbsp;<i>A</i> with the order numbers in the range&nbsp;1 to&nbsp;<i>K</i>.`
	a.en[59] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Create a new array&nbsp;<i>B</i> of the same size; each array element&nbsp;<i>B</i><sub><i>K</i></sub> must be equal to the average of elements of&nbsp;<i>A</i> with the order numbers in the range&nbsp;1 to&nbsp;<i>K</i>.`
	a.en[60] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Create a new array&nbsp;<i>B</i> of the same size; each array element&nbsp;<i>B</i><sub><i>K</i></sub> must be equal to the sum of elements of&nbsp;<i>A</i> with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>N</i>.`
	a.en[61] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Create a new array&nbsp;<i>B</i> of the same size; each array element&nbsp;<i>B</i><sub><i>K</i></sub> must be equal to the average of elements of&nbsp;<i>A</i> with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>N</i>.`
	a.en[62] = `Given an array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers, create two new arrays&nbsp;<i>B</i> and&nbsp;<i>C</i> and write all positive elements of&nbsp;<i>A</i> to the array&nbsp;<i>B</i> and all negative elements of&nbsp;<i>A</i> to the array&nbsp;<i>C</i> (in the same order). Output the size of the array&nbsp;<i>B</i> and all its elements and then output the size of the array&nbsp;<i>C</i> and all its elements.`
	a.en[63] = `Two arrays&nbsp;<i>A</i> and&nbsp;<i>B</i> of 5&nbsp;real numbers are given. Values of elements of each array are in ascending order. Write all elements of&nbsp;<i>A</i> and&nbsp;<i>B</i> to a new array&nbsp;<i>C</i> (of size&nbsp;10) so that values of all elements of&nbsp;<i>C</i> were in ascending order.`
	a.en[64] = `Three arrays&nbsp;<i>A</i>, <i>B</i>,&nbsp;<i>C</i> of&nbsp;<i>N</i><sub><i>A</i></sub>, <i>N</i><sub><i>B</i></sub>,&nbsp;<i>N</i><sub><i>C</i></sub> integers are given. Values of elements of each array are in descending order. Write all elements of&nbsp;<i>A</i>, <i>B</i>,&nbsp;<i>C</i> to a new array&nbsp;<i>D</i> (of size <i>N</i><sub><i>A</i></sub>&nbsp;+&nbsp;<i>N</i><sub><i>B</i></sub>&nbsp;+&nbsp;<i>N</i><sub><i>C</i></sub>) so that values of all elements of&nbsp;<i>D</i> were in descending order.`
	a.en[65] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Increase the value of each element of&nbsp;<i>A</i> by the initial value of&nbsp;<i>A</i><sub><i>K</i></sub>.`
	a.en[66] = `An array of <i>N</i>&nbsp;integers is given. Increase all even numbers contained in the array by the initial value of the first even number in the array. If the array does not contain even numbers then do not change it.`
	a.en[67] = `An array of <i>N</i>&nbsp;integers is given. Increase all odd numbers contained in the array by the initial value of the last odd number in the array. If the array does not contain odd numbers then do not change it.`
	a.en[68] = `Given an array of <i>N</i>&nbsp;real numbers, exchange values of its minimal and maximal element.`
	a.en[69] = `Given an array of <i>N</i>&nbsp;real numbers (<i>N</i>&nbsp;is an even number), exchange values of its first and second element, its third and fourth element, and so on.`
	a.en[70] = `Given an array of <i>N</i>&nbsp;real numbers (<i>N</i>&nbsp;is an even number), exchange values of the first half and the second half of its elements.`
	a.en[71] = `Given an array of <i>N</i>&nbsp;real numbers, change the order of its elements to inverse one.`
	a.en[72] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Change the order of the array elements between&nbsp;<i>A</i><sub><i>K</i></sub> and&nbsp;<i>A</i><sub><i>L</i></sub> (including these elements) to inverse one.`
	a.en[73] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Change the order of the array elements between&nbsp;<i>A</i><sub><i>K</i></sub> and&nbsp;<i>A</i><sub><i>L</i></sub> (not including these elements) to inverse one.`
	a.en[74] = `An array of <i>N</i>&nbsp;real numbers is given. Assign zero value to the array elements between the minimal element and the maximal element (not including these elements).`
	a.en[75] = `An array of <i>N</i>&nbsp;real numbers is given. Change the order of the array elements between the minimal element and the maximal element (including these elements) to inverse one.`
	a.en[76] = `Given an array of <i>N</i>&nbsp;real numbers, assign zero value to all its local maximums (an array element is called the <i>local maximum</i> if it is greater than its neighbors).`
	a.en[77] = `Given an array of <i>N</i>&nbsp;real numbers, replace each local minimum with its square (an array element is called the <i>local minimum</i> if it is smaller than its neighbors).`
	a.en[78] = `Given an array of <i>N</i>&nbsp;real numbers, replace each array element with the average of this element and its neighbors.`
	a.en[79] = `Given an array of <i>N</i>&nbsp;real numbers, perform the <i>right shift</i> of array elements on one position by assigning initial values of&nbsp;<i>A</i><sub>1</sub>, <i>A</i><sub>2</sub>, &#8230;, <i>A</i><sub><i>N</i>&#8722;1</sub> to elements&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>3</sub>, &#8230;, <i>A</i><sub><i>N</i></sub> respectively (an initial value of the last element will be lost). Assign zero value to the first element of the changed array.`
	a.en[80] = `Given an array of <i>N</i>&nbsp;real numbers, perform the <i>left shift</i> of array elements on one position by assigning initial values of&nbsp;<i>A</i><sub><i>N</i></sub>, <i>A</i><sub><i>N</i>&#8722;1</sub>, &#8230;, <i>A</i><sub>2</sub> to elements&nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, <i>A</i><sub><i>N</i>&#8722;2</sub>, &#8230;, <i>A</i><sub>1</sub> respectively (an initial value of the first element will be lost). Assign zero value to the last element of the changed array.`
	a.en[81] = `Given an array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>N</i>), perform the <i>right shift</i> of array elements on <i>K</i>&nbsp;positions by assigning initial values of&nbsp;<i>A</i><sub>1</sub>, <i>A</i><sub>2</sub>, &#8230;, <i>A</i><sub><i>N</i>&#8722;<i>K</i></sub> to elements&nbsp;<i>A</i><sub><i>K</i>+1</sub>, <i>A</i><sub><i>K</i>+2</sub>, &#8230;, <i>A</i><sub><i>N</i></sub> respectively (an initial value of the last <i>K</i>&nbsp;elements will be lost). Assign zero value to the first <i>K</i>&nbsp;elements of the changed array.`
	a.en[82] = `Given an array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>N</i>), perform the <i>left shift</i> of array elements on <i>K</i>&nbsp;positions by assigning initial values of&nbsp;<i>A</i><sub><i>N</i></sub>, <i>A</i><sub><i>N</i>&#8722;1</sub>, &#8230;, <i>A</i><sub><i>K</i>+1</sub> to elements&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i></sub>, <i>A</i><sub><i>N</i>&#8722;<i>K</i>&#8722;1</sub>, &#8230;, <i>A</i><sub>1</sub> respectively (an initial value of the first <i>K</i>&nbsp;elements will be lost). Assign zero value to the last <i>K</i>&nbsp;elements of the changed array.`
	a.en[83] = `Given an array of <i>N</i>&nbsp;real numbers, perform the <i>right cyclic shift</i> of array elements on one position by assigning initial values of&nbsp;<i>A</i><sub>1</sub>, <i>A</i><sub>2</sub>, &#8230;, <i>A</i><sub><i>N</i>&#8722;1</sub>, <i>A</i><sub><i>N</i></sub> to elements&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>3</sub>, &#8230;, <i>A</i><sub><i>N</i></sub>, <i>A</i><sub>1</sub> respectively.`
	a.en[84] = `Given an array of <i>N</i>&nbsp;real numbers, perform the <i>left cyclic shift</i> of array elements on one position by assigning initial values of&nbsp;<i>A</i><sub><i>N</i></sub>, <i>A</i><sub><i>N</i>&#8722;1</sub>, &#8230;, <i>A</i><sub>2</sub>, <i>A</i><sub>1</sub> to elements&nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, <i>A</i><sub><i>N</i>&#8722;2</sub>, &#8230;, <i>A</i><sub>1</sub>, <i>A</i><sub><i>N</i></sub> respectively.`
	a.en[85] = `Given an array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;4, <i>K</i>&nbsp;&lt;&nbsp;<i>N</i>), perform the <i>right cyclic shift</i> of array elements on <i>K</i>&nbsp;positions by assigning initial values of&nbsp;<i>A</i><sub>1</sub>, <i>A</i><sub>2</sub>, &#8230;, <i>A</i><sub><i>N</i></sub> to elements&nbsp;<i>A</i><sub><i>K</i>+1</sub>, <i>A</i><sub><i>K</i>+2</sub>, &#8230;, <i>A</i><sub><i>K</i></sub> respectively. An additional array of 4&nbsp;elements may be used for performing the required shift.`
	a.en[86] = `Given an array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;4, <i>K</i>&nbsp;&lt;&nbsp;<i>N</i>), perform the <i>left cyclic shift</i> of array elements on <i>K</i>&nbsp;positions by assigning initial values of&nbsp;<i>A</i><sub><i>N</i></sub>, <i>A</i><sub><i>N</i>&#8722;1</sub>, &#8230;, <i>A</i><sub>1</sub> to elements&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i></sub>, <i>A</i><sub><i>N</i>&#8722;<i>K</i>&#8722;1</sub>, &#8230;, <i>A</i><sub><i>N</i>&#8722;<i>K</i>+1</sub> respectively. An additional array of 4&nbsp;elements may be used for performing the required shift.`
	a.en[87] = `An array of <i>N</i>&nbsp;real numbers is given. The values of all array elements, except the first one, are in ascending order. Arrange all array elements in ascending order by moving the first element to a new position.`
	a.en[88] = `An array of <i>N</i>&nbsp;real numbers is given. The values of all array elements, except the last one, are in ascending order. Arrange all array elements in ascending order by moving the last element to a new position.`
	a.en[89] = `An array of <i>N</i>&nbsp;real numbers is given. The values of all array elements, except one element, are in descending order. Arrange all array elements in descending order by moving the element, that violates ordering, to a new position.`
	a.en[90] = `An array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Remove an element with the order number&nbsp;<i>K</i> from the array.`
	a.en[91] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Remove elements with the order numbers in the range&nbsp;<i>K</i> to&nbsp;<i>L</i> inclusively from the array and output the size of the changed array and all its elements.`
	a.en[92] = `Given an array of <i>N</i>&nbsp;integers, remove all odd numbers from the array and output the size of the changed array and all its elements.`
	a.en[93] = `Given an array of&nbsp;<i>N</i> (&gt;&nbsp;2) integers, remove all elements with even order numbers (2,&nbsp;4,&nbsp;&#8230;) from the array. Do not use conditional statements.`
	a.en[94] = `Given an array of&nbsp;<i>N</i> (&gt;&nbsp;2) integers, remove all elements with odd order numbers (1,&nbsp;3,&nbsp;&#8230;) from the array. Do not use conditional statements.`
	a.en[95] = `Given an array of <i>N</i>&nbsp;integers, remove all successive equal elements (except for their first occurrence) from the array.`
	a.en[96] = `Given an array of <i>N</i>&nbsp;integers, remove all equal elements (except for their first occurrence) from the array.`
	a.en[97] = `Given an array of <i>N</i>&nbsp;integers, remove all equal elements (except for their last occurrence) from the array.`
	a.en[98] = `Given an array of <i>N</i>&nbsp;integers, remove all elements whose values appear less than three times in the array. Output the size of the changed array and all its elements.`
	a.en[99] = `Given an array of <i>N</i>&nbsp;integers, remove all elements whose values appear more than twice in the array. Output the size of the changed array and all its elements.`
	a.en[100] = `Given an array of <i>N</i>&nbsp;integers, remove all elements whose values appear exactly twice in the array. Output the size of the changed array and all its elements.`
	a.en[101] = `An array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Insert a new element with zero value before an element with the order number&nbsp;<i>K</i>.`
	a.en[102] = `An array of <i>N</i>&nbsp;real numbers and an integer&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>) are given. Insert a new element with zero value after an element with the order number&nbsp;<i>K</i>.`
	a.en[103] = `An array of <i>N</i>&nbsp;real numbers is given. Insert new elements with zero value before an element with the minimal value and after an element with the maximal value.`
	a.en[104] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>M</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>, 1&nbsp;&#8804;&nbsp;<i>M</i>&nbsp;&#8804;&nbsp;10) are given. Insert <i>M</i>&nbsp;new elements with zero value before an element with the order number&nbsp;<i>K</i>.`
	a.en[105] = `An array of <i>N</i>&nbsp;real numbers and two integers&nbsp;<i>K</i> and&nbsp;<i>M</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>, 1&nbsp;&#8804;&nbsp;<i>M</i>&nbsp;&#8804;&nbsp;10) are given. Insert <i>M</i>&nbsp;new elements with zero value after an element with the order number&nbsp;<i>K</i>.`
	a.en[106] = `Given an array of <i>N</i>&nbsp;real numbers, double occurrences of elements with even order numbers (2,&nbsp;4,&nbsp;&#8230;). Do not use conditional statements in loops.`
	a.en[107] = `Given an array of <i>N</i>&nbsp;real numbers, triple occurrences of elements with odd order numbers (1,&nbsp;3,&nbsp;&#8230;). Do not use conditional statements in loops.`
	a.en[108] = `Given an array of <i>N</i>&nbsp;real numbers, insert an element with zero value before each element with positive value.`
	a.en[109] = `Given an array of <i>N</i>&nbsp;real numbers, insert an element with zero value after each element with negative value.`
	a.en[110] = `Given an array of <i>N</i>&nbsp;integers, double occurrences of all elements whose values are even numbers.`
	a.en[111] = `Given an array of <i>N</i>&nbsp;integers, triple occurrences of all elements whose values are odd numbers.`
	a.en[112] = `An array&nbsp;<i>A</i> of&nbsp;<i>N</i> (&#8804;&nbsp;6) real numbers is given. Sort the array in ascending order by using the <i>exchange sort method</i> (the <i>bubble sorting</i>): compare each pair of two adjacent elements (<i>A</i><sub>1</sub> and&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>2</sub> and&nbsp;<i>A</i><sub>3</sub>, and so on) and exchange their values in case the left element is greater than the right one; repeat <i>N</i>&nbsp;&#8722;&nbsp;1 times such array pass. Output all array elements after each pass for checking results. Note that the amount of analyzed pairs of the array elements can be reduced by&nbsp;1 after each pass.`
	a.en[113] = `An array&nbsp;<i>A</i> of&nbsp;<i>N</i> (&#8804;&nbsp;6) real numbers is given. Sort the array in ascending order by using the <i>selection sort method</i>: find the greatest element in the array and exchange the values of the greatest element and the last element (with the order number&nbsp;<i>N</i>); repeat <i>N</i>&nbsp;&#8722;&nbsp;1 times this process, reducing the amount of analyzed elements by 1 after each array pass. Output all array elements after each pass for checking results.`
	a.en[114] = `An array&nbsp;<i>A</i> of&nbsp;<i>N</i> (&#8804;&nbsp;6) real numbers is given. Sort the array in ascending order by using the <i>insertion sort method</i>: compare an element&nbsp;<i>A</i><sub>2</sub> with the first element&nbsp;<i>A</i><sub>1</sub> and exchange their values, if necessary, so that these elements were in ascending order; then move an element&nbsp;<i>A</i><sub>3</sub> to the left (sorted) part of the array, so that three elements were in ascending order; repeat this process for other array elements. Output all array elements after processing of each element (from&nbsp;<i>A</i><sub>2</sub> to&nbsp;<i>A</i><sub><i>N</i></sub>). Note that it is convenient to assign the array element being processed to an additional array element&nbsp;<i>A</i><sub>0</sub> (the <i>barrier element</i>).`
	a.en[115] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;real numbers is given. Without changing the array&nbsp;<i>A</i>, output the order numbers that correspond to array elements in ascending order  of their values. For solving the task create an additional <i>index array</i>&nbsp;<i>I</i>, which contains order numbers in the range&nbsp;1 to&nbsp;<i>N</i>, and use the bubble sorting (see Array112) as follows: compare elements of array A (with the order numbers&nbsp;<i>I</i><sub>1</sub> and&nbsp;<i>I</i><sub>2</sub>, <i>I</i><sub>2</sub> and&nbsp;<i>I</i><sub>3</sub>, and so on) and exchange, if necessary, values of corresponding elements of index array&nbsp;<i>I</i>. After repeating <i>N</i>&nbsp;&#8722;&nbsp;1&nbsp;times of such array pass the required sequence of order numbers will be contained in the array&nbsp;<i>I</i>.`
	a.en[116] = `An array&nbsp;<i>A</i> of <i>N</i>&nbsp;integers is given. A group of successive array elements with equal values is called a <i>series of equal numbers</i>, the amount of its elements is called a <i>length of series</i> (a length of series may be equal to&nbsp;1), the value of its elements is called a <i>value of series</i>. Create and output two new integer-valued arrays&nbsp;<i>B</i> and&nbsp;<i>C</i> containing respectively lengths and values of all series of equal numbers of the array&nbsp;<i>A</i>.`
	a.en[117] = `Given an array of <i>N</i>&nbsp;integers, insert an element with zero value before each series of equal numbers of the array (see the <i>series</i> definition in Array116).`
	a.en[118] = `Given an array of <i>N</i>&nbsp;integers, insert an element with zero value after each series of equal numbers of the array (see the <i>series</i> definition in Array116).`
	a.en[119] = `Given an array of <i>N</i>&nbsp;integers, increase each series of equal numbers of the array by one element (see the <i>series</i> definition in Array116).`
	a.en[120] = `An array of <i>N</i>&nbsp;integers is given. The array contains at least one series of two or more equal numbers. Decrease each series of equal numbers of the array by one element (see the <i>series</i> definition in Array116).`
	a.en[121] = `An integer&nbsp;<i>K</i> (&gt;&nbsp;0) and an array of <i>N</i>&nbsp;integers are given. Double the length of the <i>K</i>-th series of equal numbers of the array (see the <i>series</i> definition in Array116). If the array contains less than <i>K</i>&nbsp;series then do not change it.`
	a.en[122] = `An integer&nbsp;<i>K</i> (&gt;&nbsp;1) and an array of <i>N</i>&nbsp;integers are given. Remove the <i>K</i>-th series of equal numbers from the array (see the <i>series</i> definition in Array116). If the array contains less than <i>K</i>&nbsp;series then do not change it.`
	a.en[123] = `An integer&nbsp;<i>K</i> (&gt;&nbsp;1) and an array of <i>N</i>&nbsp;integers are given. Exchange the first and the <i>K</i>-th series of equal numbers of the array (see the <i>series</i> definition in Array116). If the array contains less than <i>K</i>&nbsp;series then do not change it.`
	a.en[124] = `An integer&nbsp;<i>K</i> (&gt;&nbsp;1) and an array of <i>N</i>&nbsp;integers are given. Exchange the last and the <i>K</i>-th series of equal numbers of the array (see the <i>series</i> definition in Array116). If the array contains less than <i>K</i>&nbsp;series then do not change it.`
	a.en[125] = `An integer&nbsp;<i>L</i> (&gt;&nbsp;1) and an array of <i>N</i>&nbsp;integers are given. Replace each series, whose length is less than&nbsp;<i>L</i>, by one element with zero value (see the <i>series</i> definition in Array116).`
	a.en[126] = `An integer&nbsp;<i>L</i> (&gt;&nbsp;0) and an array of <i>N</i>&nbsp;integers are given. Replace each series, whose length is equal to&nbsp;<i>L</i>, by one element with zero value (see the <i>series</i> definition in Array116).`
	a.en[127] = `An integer&nbsp;<i>L</i> (&gt;&nbsp;0) and an array of <i>N</i>&nbsp;integers are given. Replace each series, whose length is greater than&nbsp;<i>L</i>, by one element with zero value (see the <i>series</i> definition in Array116).`
	a.en[128] = `Given an array of <i>N</i>&nbsp;integers, increase its first series of the maximal length by one element (see the <i>series</i> definition in Array116).`
	a.en[129] = `Given an array of <i>N</i>&nbsp;integers, increase its last series of the maximal length by one element (see the <i>series</i> definition in Array116).`
	a.en[130] = `Given an array of <i>N</i>&nbsp;integers, increase all its series of the maximal length by one element (see the <i>series</i> definition in Array116).`
	a.en[131] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane and a point&nbsp;<i>B</i> are given (all points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the point of&nbsp;<i>A</i> that is the nearest to the point&nbsp;<i>B</i>. The <i>distance</i>&nbsp;<i>R</i> between two points with the coordinates (<i>x</i><sub>1</sub>,&nbsp;<i>y</i><sub>1</sub>) and&nbsp;(<i>x</i><sub>2</sub>,&nbsp;<i>y</i><sub>2</sub>) may be found by formula:<i>R</i>&nbsp;=&nbsp;((<i>x</i><sub>2</sub>&nbsp;&#8722;&nbsp;<i>x</i><sub>1</sub>)<sup>2</sup>&nbsp;+&nbsp;(<i>y</i><sub>2</sub>&nbsp;&#8722;&nbsp;<i>y</i><sub>1</sub>)<sup>2</sup>)<sup>1/2</sup>.`
	a.en[132] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane is given (points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the point of&nbsp;<i>A</i> that lies in the second coordinate quarter and is the farthest from the origin. If the set&nbsp;<i>A</i> does not contain such points then output the origin&nbsp;(0,&nbsp;0).`
	a.en[133] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane is given (points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the point of&nbsp;<i>A</i> that lies in the first or the third coordinate quarter and is the nearest to the origin. If the set&nbsp;<i>A</i> does not contain such points then output the origin&nbsp;(0,&nbsp;0).`
	a.en[134] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane is given (points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find two points of&nbsp;<i>A</i> with the maximal distance between them and output these points (in ascending order of their indices in the set&nbsp;<i>A</i>) and the value of the distance.`
	a.en[135] = `Two sets&nbsp;<i>A</i> and&nbsp;<i>B</i> of&nbsp;<i>N</i><sub>1</sub> and&nbsp;<i>N</i><sub>2</sub> points respectively are given (points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the point of&nbsp;<i>A</i> and the point of&nbsp;<i>B</i> with the minimal distance between them. Output the value of the distance and then the point of&nbsp;<i>A</i> and the point of&nbsp;<i>B</i>.`
	a.en[136] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane is given (<i>N</i>&nbsp;&gt;&nbsp;2, points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the point of&nbsp;<i>A</i> such that the sum of distances between this point and other points of&nbsp;<i>A</i> is minimal and output this point and the corresponding sum.`
	a.en[137] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane is given (<i>N</i>&nbsp;&gt;&nbsp;2, points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the maximal perimeter of a triangle with vertices belonging to&nbsp;<i>A</i>. Output this perimeter and the corresponding vertices (in ascending order of their indices in the set&nbsp;<i>A</i>).`
	a.en[138] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points in the coordinate plane is given (<i>N</i>&nbsp;&gt;&nbsp;2, points are determined by their coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i>). Find the minimal perimeter of a triangle with vertices belonging to&nbsp;<i>A</i>. Output this perimeter and the corresponding vertices (in ascending order of their indices in the set&nbsp;<i>A</i>).`
	a.en[139] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points with integer-valued coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i> is given. The <i>order in the plane</i> is defined as follows: (<i>x</i><sub>1</sub>,&nbsp;<i>y</i><sub>1</sub>)&nbsp;&lt;&nbsp;(<i>x</i><sub>2</sub>,&nbsp;<i>y</i><sub>2</sub>), &nbsp; &nbsp;if either <i>x</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>x</i><sub>2</sub> or <i>x</i><sub>1</sub>&nbsp;=&nbsp;<i>x</i><sub>2</sub> and <i>y</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>y</i><sub>2</sub>.Using this order definition, rearrange points of&nbsp;<i>A</i> in ascending order.`
	a.en[140] = `A set&nbsp;<i>A</i> of <i>N</i>&nbsp;points with integer-valued coordinates&nbsp;<i>x</i>,&nbsp;<i>y</i> is given. The <i>order in the plane</i> is defined as follows: (<i>x</i><sub>1</sub>,&nbsp;<i>y</i><sub>1</sub>)&nbsp;&lt;&nbsp;(<i>x</i><sub>2</sub>,&nbsp;<i>y</i><sub>2</sub>), &nbsp; &nbsp;if either <i>x</i><sub>1</sub>&nbsp;+&nbsp;<i>y</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>x</i><sub>2</sub>&nbsp;+&nbsp;<i>y</i><sub>2</sub> or <i>x</i><sub>1</sub>&nbsp;+&nbsp;<i>y</i><sub>1</sub>&nbsp;=&nbsp;<i>x</i><sub>2</sub>&nbsp;+&nbsp;<i>y</i><sub>2</sub> and <i>x</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>x</i><sub>2</sub>.Using this order definition, rearrange points of&nbsp;<i>A</i> in descending order.`
}

func (a *aarray) makeTj() {
	a.tj = make([]string, a.count)
	a.tj[1] = `Адади бутуни N(>0) дода шудааст. Массиви типи бутуни андозаи N-ро тартиб дода, хориҷ кунед, ки N-то ададҳои тоқи мусбии аввалинро доро бошад: 1, 3, 5, … .`
	a.tj[2] = `Адади бутуни N(>0) дода шудааст. Массиви типи бутуни андозаи N-ро тартиб дода, хориҷ кунед, ки дараҷаҳои адади 2(ду)-ро аз якӯм то N-ӯм доро бошад: 2, 4, 8, 16, … .`
	a.tj[3] = `Адади бутуни N(>1), ҳамчунин аъзои аввалин A ва фарқи прогрессияи арифметикӣ D дода шудаанд. Массиви андозаи N-ро тартиб дода, хориҷ кунед, ки N-то аъзоҳои аввалаи прогрессияи зеринро доро бошад: A, A+D, A+2*D, A+3*D, … .`
	a.tj[4] = `Адади бутуни N(>1), ҳамчунин аъзои аввала A ва маъраҷи прогрессияи геометрӣ Q дода шудаанд. Массиви андозаи N-ро тартиб дода, хориҷ кунед, ки N-то аъзоҳои аввалаи прогрессияи зеринро доро бошад: A, A*Q, A*Q<sup>2</sup>, A*Q<sup>3</sup>, … .`
	a.tj[5] = `Адади бутуни N (>2) дода шудааст. Массиви типи бутуни андозаи N-ро тартиб дода, хориҷ кунед, ки N-то элементҳои аввалаи пайдарпайии ададҳои Фибоначчиро доро бошад: F[k]: F[1]=1, F[2]=1, F[k]=F[k–2]+F[k–1], k=3, 4, … .`
	a.tj[6] = `Ададҳои бутуни N(>2), A ва B дода шудаанд. Массиви типи бутуни андозаи N-ро тартиб дода, хориҷ кунед, ки элементи якӯми он баробари A буда, дуюмаш баробари B аст ва ҳар як элементи оянда баробари суммаи ҳамаи элементҳои пешина аст.`
	a.tj[7] = `Массиви андозаи N дода шудааст. Элементҳои онро бо тартиби баръакс хориҷ кунед.`
	a.tj[8] = `Массиви типи бутуни андозаи N дода шудааст. Ҳамаи ададҳои тоқи дар массиви мазкур мавҷударо бо тартиби афзуншавии рақамҳои тартибиашон хориҷ кунед ва ҳамчунин миқдори онҳоро низ хориҷ кунед.`
	a.tj[9] = `Массиви типи бутуни андозаи N дода шудааст. Ҳамаи ададҳои ҷуфти дар массиви мазкур мавҷударо бо тартиби камшавии рақамҳои тартибиашон хориҷ кунед ва ҳамчунин миқдори онҳоро низ хориҷ кунед.`
	a.tj[10] = `Массиви типи бутуни андозаи N дода шудааст. Дар аввал ҳамаи ададҳои ҷуфти дар массиви мазкур мавҷударо бо тартиби афзуншавии рақамҳои тартибиашон, сонӣ ҳамаи ададҳои тоқро бо тартиби камшавии рақамҳои тартибиашон хориҷ кунед.`
	a.tj[11] = `Массиви A бо андозаи N ва адади бутуни K (1≤K≤N) дода шудаанд. Элементҳои массивро бо рақамҳои тартибии ба адади K каратӣ хориҷ кунед: A[k], A[2*k], A[3*k], … . Оператори шартиро истифода набаред.`
	a.tj[12] = `Массиви A бо андозаи N (N - адади ҷуфт) дода шудааст. Элементҳои онро, ки дорои рақамҳои тартибии ҷуфт ҳастанд, бо тартиби афзуншавии рақамҳои тартибӣ хориҷ кунед: A[2], A[4], A[6], .., A[n]. Оператори шартиро истифода набаред.`
	a.tj[13] = `Массиви A бо андозаи N (N — адади тоқ) дода шудааст. Элементҳои онро, ки дорои рақамҳои тартибии тоқ ҳастанд, бо тартиби камшавии рақамҳои тартибӣ хориҷ кунед: A[n], A[n-2], A[n-4], …,A[1]. Оператори шартиро истифода набаред.`
	a.tj[14] = `Массиви A бо андозаи N дода шудааст. Дар аввал элементҳои онро, ки дорои рақамҳои тартибии ҷуфт ҳастанд (бо тартиби афзуншавии рақамҳои тартибӣ), сонӣ элементҳоеро, ки дорои рақамҳои тартибии тоқ ҳастанд (ҳамчунин бо тартиби афзуншавии рақамҳои тартибӣ), хориҷ кунед: A[2], A[4], A[6], …, A[1], A[3], A[5], … .`
	a.tj[15] = `Массиви A бо андозаи N дода шудааст. Дар аввал элементҳои онро, ки дорои рақамҳои тартибии тоқ ҳастанд, бо тартиби афзуншавии рақамҳои тартибӣ, сонӣ элементҳоеро, ки дорои рақамҳои тартибии ҷуфт ҳастанд, бо тартиби камшавии рақамҳои тартибӣ хориҷ кунед. A[1], A[3], A[5], .., A[6], A[4], A[2]. Оператори шартиро истифода набаред.`
	a.tj[16] = `Массиви A бо андозаи N дода шудааст. Элементҳои онро бо тартиби зерин хориҷ кунед: A[1], A[n], A[2], A[n-1], A[3], A[n-2], .. .`
	a.tj[17] = `Массиви A бо андозаи N дода шудааст. Элементҳои онро бо тартиби зерин хориҷ кунед: A[1], A[2], A[n], A[n-1], A[3], A[4], A[n-2], A[n-3], .. .`
	a.tj[18] = `Массиви A, ки дорои ададҳои ғайринулӣ аст, бо андозаи 10 дода шудааст. Қимати элементи аввалини онро аз он элементҳои A[k]-и он, ки нобаробарии A[k]&lt;A[10]-ро қаноат мекунонанд, хориҷ кунед. Агар чунин элемент набошад, пас 0(нул)-ро хориҷ кунед.`
	a.tj[19] = `Массиви типи бутуни A бо андозаи 10 дода шудааст. Рақами тартибии элементи охиринро аз он элементҳои A[k]-и он, ки нобаробарии дукаратаи A[1]&lt;A[k]&lt;A[10]-ро қаноат мекунонанд, хориҷ кунед. Агар чунин элемент набошад, пас 0(нул)-ро хориҷ кунед.`
	a.tj[20] = `Массиви андозаи N ва ададҳои бутуни K ва L (1≤K≤L≤N) дода шудаанд. Суммаи элементҳои массивро бо рақамҳои тартибии аз K то L дар якҷоягӣ ёбед.`
	a.tj[21] = `Массиви андозаи N ва ададҳои бутуни K ва L (1≤K≤L≤N) дода шудаанд. Қимати миёнаи арифметикии элементҳои массивро бо рақамҳои тартибии аз K то L дар якҷоягӣ ёбед.`
	a.tj[22] = `Массиви андозаи N ва ададҳои бутуни K ва L (1&lt;K≤L≤N) дода шудаанд. Суммаи ҳамаи элементҳои массивро, ба ғайр аз элементҳо бо рақамҳои тартибии аз K то L дар якҷоягӣ ёбед.`
	a.tj[23] = `Массиви андозаи N ва ададҳои бутуни K ва L (1&lt;K≤L≤N) дода шудаанд. Қимати миёнаи арифметикии ҳамаи элементҳои массивро, ба ғайр аз элементҳо бо рақамҳои тартибии аз K то L дар якҷоягӣ ёбед.`
	a.tj[24] = `Массиви типи бутун бо андозаи N, ки ададҳои якхела надорад, дода шудааст. Санҷед, ки элементҳои он прогрессияи арифметикиро ташкил медиҳанд ё не (ниг. ба масъалаи Array3). Агар ташкил диҳанд, пас фарқи прогрессияро хориҷ кунед, вагарна — 0(нул)-ро хориҷ кунед.`
	a.tj[25] = `Массиви ададҳои бутуни ғайринулӣ бо андозаи N дода шудааст. Санҷед, ки элементҳои он прогресияи геометриро ташкил медиҳанд ё не (ниг. ба масъалаи Array4). Агар ташкил диҳанд, пас махраҷи прогрессияро хориҷ кунед, вагарна — 0(нул)-ро хориҷ кунед.`
	a.tj[26] = `Массиви типи бутуни андозаи N дода шудааст. Санҷед, ки дар он ададҳои ҷуфт ва тоқ пайдарпай меоянд ё не. Агар пайдарпай оянд, пас 0(нул)-ро хориҷ кунед, вагарна рақами тартибии элементи аввалини вайронкунандаи қонуниятро хориҷ кунед.`
	a.tj[27] = `Массиви ададҳои бутуни ғайринулӣ бо андозаи N дода шудааст. Санҷед, ки дар он ададҳои мусбӣ ва манфӣ пайдарпай меоянд ё не. Агар пайдарпай оянд, пас 0(нул)-ро хориҷ кунед, вагарна рақами тартибии элементи аввалини вайронкунандаи қонуниятро хориҷ кунед.`
	a.tj[28] = `Массиви A бо андозаи N дода шудааст. Элементи хурдтаринро аз дохили он элементҳои массив, ки дорои рақамҳои тартибии ҷуфт ҳастанд, ёбед: A[2], A[4], A[6], ... .`
	a.tj[29] = `Массиви A бо андозаи N дода шудааст. Элементи калонтаринро аз дохили он элементҳои массив, ки дорои рақамҳои тартибии тоқ ҳастанд, ёбед: A[1], A[3], A[5], ... .`
	a.tj[30] = `Массиви андозаи N дода шудааст. Рақамҳои тартибии он элементҳои массивро, ки аз ҳамсояи рости худ калон ҳастанд, ва миқдори чунин элементҳоро ёбед. Рақамҳои тартибии ёфташударо бо тартибии афзуншавиашон хориҷ кунед.`
	a.tj[31] = `Массиви андозаи N дода шудааст. Рақамҳои он элементҳои массивро, ки онҳо аз ҳамсояи чапи худ калонанд ва миқдори чунин элементҳоро ёбед. Рақамҳои ёфташуда бо тартиби камшавиашон хориҷ карда шаванд.`
	a.tj[32] = `Массиви андозаи N дода шудааст. Рақами элементи хурдтарини аввалини локалӣ (элементи хурдтарини локалӣ — ин элементест, ки аз дилхоҳ ҳамсояҳои худ хурд мебошад)-ро ёбед.`
	a.tj[33] = `Массиви андозаи N дода шудааст. Рақами элементи калонтарини охирини локалӣ (элементи калонтарини локалӣ — ин элементест, ки аз дилхоҳ ҳамсояҳои худ калон мебошад)-ро ёбед.`
	a.tj[34] = `Массиви андозаи N дода шудааст. Аз элементҳои хурдтарини локалӣ (муайянкунии элементи хурдтарини локалӣ дар масъалаи Array32 дода шудааст)-и массив калонтаринашро ёбед.`
	a.tj[35] = `Массиви андозаи N дода шудааст. Аз элементҳои калонтарини локалӣ (муайянкунии элементи калонтарини локалӣ дар масъалаи Array33 дода шудааст)-и массив хурдтаринашро ёбед.`
	a.tj[36] = `Массиви андозаи N дода шудааст. Аз он элементҳои массив, ки на элементи хурдтарини локалӣ ва на элементи калонтарини локалӣ мебошанд, калонтаринашро ёбед (муайянкунии элементҳои калонтарин ва хурдтарини локалӣ дар масъалаҳои Array32 ва Array33 дода шудаанд). Агар дар массив чунин элемент набошад, пас 0(нул)-ро хориҷ кунед.`
	a.tj[37] = `Массиви андозаи N дода шудааст. Миқдори қитъаҳоеро, ки дар онҳо элементҳои массив монотонӣ афзуншаванда мебошанд, ёбед.`
	a.tj[38] = `Массиви андозаи N дода шудааст. Миқдори қитъаҳоеро, ки дар онҳо элементҳои массив монотонӣ камшаванда мебошанд, ёбед.`
	a.tj[39] = `Массиви андозаи N дода шудааст. Миқдори фосилаҳои байни монотонигиҳо (яъне қитъаҳое, ки дар онҳо элементҳои массив афзун ё кам мешаванд)-и онро ёбед.`
	a.tj[40] = `Адади R ва массиви A бо андозаи N дода шудаанд. Элементи массивро, ки ба адади R наздиктарин аст (яъне чунин элементи A[k], ки барои он бузургии |A[k]-R| хурдтарин аст), ёбед.`
	a.tj[41] = `Массиви андозаи N дода шудааст. Ду элементҳои ҳамсояро ёбед, ки суммаи онҳо хурдтарин аст ва ин элементҳоро бо тартиби афзуншавии рақамҳои тартибиашон хориҷ кунед.`
	a.tj[42] = `Адади R ва массиви андозаи N дода шудаанд. Ду элементҳои ҳамсояи массивро ёбед, ки суммаи онҳо ба адади R наздиктарин аст ва ин элементҳоро бо тартиби афзуншавии рақамҳои тартибиашон хориҷ кунед (муайянкунии ададҳои наздиктарин дар масъалаи Array40 дода шудааст).`
	a.tj[43] = `Массиви типи бутуни андозаи N дода шудааст, ки ҳамаи элементҳои он ба тартиб гузошта шудаанд (аз рӯи афзуншавӣ ё камшавӣ). Миқдори элементҳои фарқкунандаро дар массиви мазкур ёбед.`
	a.tj[44] = `Массиви типи бутуни андозаи N дода шудааст, ки расо ду элементи якхела дорад. Рақамҳои тартибии элементҳои якхеларо ёбед ва ин рақамҳоро бо тартиби афзуншавӣ хориҷ кунед.`
	a.tj[45] = `Массиви андозаи N дода шудааст. Рақамҳои тартибии ду элементҳои наздиктаринро (яъне элементҳоеро, ки қимати мутлақи хурдтарин аз фарқро доростанд) аз ин массив ёбед ва ин рақамҳоро бо тартиби афзуншавӣ хориҷ кунед.`
	a.tj[46] = `Адади R ва массиви андозаи N дода шудаанд. Ду элементи фарқкунандаи массивро ёбед, ки суммаи онҳо ба адади R наздиктарин аст ва ин элементҳоро бо тартиби афзуншавии рақамҳои тартибиашон хориҷ кунед (муайянкунии ададҳои наздиктарин дар масъалаи Array40 дода шудааст).`
	a.tj[47] = `Массиви типи бутуни андозаи N дода шудааст. Миқдори элементҳои фарқкунандаро дар массиви мазкур ёбед.`
	a.tj[48] = `Массиви типи бутуни андозаи N дода шудааст. Миқдори калонтарини элементҳои якхелаи онро ёбед.`
	a.tj[49] = `Массиви типи бутуни андозаи N дода шудааст. Агар он ҷобаҷо гузошта шуда бошад, яъне ҳамаи ададҳоро аз 1 то N доро бошад, пас 0(нул)-ро хориҷ кунед; дар ҳолати акс рақами тартибии элементи аввалини нораворо хориҷ кунед.`
	a.tj[50] = `Массиви типи бутуни A бо андозаи N дода шудааст, ки ҷобаҷогузошташуда мебошад (муайянкунии ҷобаҷогузорӣ дар масъалаи Array49 дода шудааст). Миқдори ивазшавиҳоро дар ҷобаҷогузории мазкур ёбед, яъне чунин ҷуфти элементҳои A[i] ва A[j]-ро, ки дар онҳо адади калонтарин аз тарафи чапи адади хурдтарин ҷойгир аст: A[i]>A[j] ҳангоми i&lt;j.`
	a.tj[51] = `Массивҳои A ва B бо андозаи якхелаи N дода шудаанд. Ҷойҳои элементҳои онҳоро иваз кунед ва дар аввал элементҳои ташкилдиҳандаи массиви A-ро хориҷ кунед, сонӣ — элементҳои ташкилдиҳандаи массиви B-ро хориҷ кунед.`
	a.tj[52] = `Массиви A бо андозаи N дода шудааст. Массиви нави B-ро бо ҳамон андоза тартиб диҳед, ки элементҳои он ба тариқи зайл муайян карда мешаванд:
 B[k]=2*A[k], агар A[k]<5 бошад;
 B[k]=A[k]/2 дар ҳолати акс`
	a.tj[53] = `Ду массивҳои A ва B бо андозаи якхелаи N дода шудаанд. Массиви нави C-ро бо ҳамон андоза тартиб диҳед, ки ҳар як элементи он ба элементи калонтарини массивҳои A ва B бо ҳамон рақами тартибӣ баробар аст.`
	a.tj[54] = `Массиви типи бутуни A бо андозаи N дода шудааст. Ба массиви нави типи бутуни B ҳамаи ададҳои тоқро аз массиви ибтидоӣ (бо ҳамон тартиб) нусхабардорӣ кунед ва андозаи массиви бадастовардашудаи B ва таркиби онро хориҷ кунед.`
	a.tj[55] = `Массиви типи бутуни A бо андозаи N(<=15) дода шудааст. Ба массиви нави типи бутуни B ҳамаи элементҳои дорои рақамҳои тартибии тоқро (1,3,...) нусхабардорӣ кунед ва андозаи массиви бадастовардашудаи B ва таркиби онро хориҷ кунед. Оператори шартиро истифода набаред.`
	a.tj[56] = `Массиви типи бутуни A бо андозаи N (<=15) дода шудааст. Ба массиви нави типи бутуни B ҳамаи элементҳои дорои рақамҳои тартибии ба адади 3 каратиро (3,6,...) нусхабардорӣ кунед ва андозаи массиви бадастовардашудаи B ва таркиби онро хориҷ кунед. Оператори шартиро истифода набаред.`
	a.tj[57] = `Массиви типи бутуни A бо андозаи N дода шудааст. Ба массиви нави типи бутуни B, ки дорои ҳамон андоза аст, аввал ҳамаи элементҳои массиви ибтидоиро бо рақамҳои тартибии ҷуфт, сонӣ - бо рақамҳои тартибии тоқ нусхабардорӣ кунед: A[2], A[4], A[6], ..., A[1], A[3], A[5], ... . Оператори шартиро истифода набаред.`
	a.tj[58] = `Массиви A бо андозаи N дода шудааст. Массиви нави B-ро бо ҳамон андоза аз рӯи қонунияти зерин тартиб диҳед: элементи B[k] ба суммаи элементҳои массиви A бо рақамҳои тартибии аз 1 то K баробар аст.`
	a.tj[59] = `Массиви A бо андозаи N дода шудааст. Массиви нави B-ро бо ҳамон андоза аз рӯи қонунияти зерин тартиб диҳед: элементи B[k] ба қимати миёнаи арифметикии элементҳои массиви A бо рақамҳои тартибии аз 1 то K баробар аст.`
	a.tj[60] = `Массиви A бо андозаи N дода шудааст. Массиви нави B-ро бо ҳамон андоза аз рӯи қонунияти зерин тартиб диҳед: элементи B[k] ба суммаи элементҳои массиви A бо рақамҳои тартибии аз K то N баробар аст.`
	a.tj[61] = `Массиви A бо андозаи N дода шудааст. Массиви нави B-ро бо ҳамон андоза аз рӯи қонунияти зерин тартиб диҳед: элементи B[k] ба қимати миёнаи арифметикии элементҳои массиви A бо рақамҳои тартибии аз K то N баробар аст.`
	a.tj[62] = `Массиви A бо андозаи N дода шудааст. Ду массивҳои нави B ва C-ро тартиб диҳед: ба массиви B ҳамаи элементҳои мусбии массиви A-ро нависед, ба массиви C бошад, ҳамаи элементҳои манфиро нависед (тартиби аввалаи пайиҳамойии элементҳоро нигоҳ доред). Дар аввал андоза ва таркиби массиви B-ро хориҷ кунед, сонӣ - андоза ва таркиби массиви C-ро хориҷ кунед.`
	a.tj[63] = `Ду массивҳои A ва B бо андозаи 5 дода шудаанд, ки элементҳои онҳо афзуншаванда ба тартиб гузошта шудаанд. Ин массивҳоро чунон якҷоя кунед, ки массиви натиҷавии C (бо андозаи 10) афзуншаванда батартибгузошташуда боқӣ монад.`
	a.tj[64] = `Се массивҳои типи бутуни A, B ва C бо андозаҳои Na, Nb, Nc мувофиқан дода шудаанд, ки элементҳои онҳо камшаванда ба тартиб гузошта шудаанд. Ин массивҳоро чунон ба тартиб гузоред, ки массиви типи бутуни натиҷавии D (бо андозаи Na+Nb+Nc) камшаванда батартибгузошташуда боқӣ монад.`
	a.tj[65] = `Массиви A бо андозаи N ва адади бутуни K (1<=K<=N) дода шудаанд. Массивро тағйир диҳед, яъне ҳар як элементи онро ба қимати ибтидоии элементи A[k] ҷамъ кунед.`
	a.tj[66] = `Массиви типи бутун бо андозаи N дода шудааст. Ҳамаи ададҳои ҷуфти массивро ба қимати ибтидоии адади ҷуфти аввала ҷамъ кунед. Агар дар массив адади ҷуфт набошад, пас массивро бе дигаргунӣ гузоред.`
	a.tj[67] = `Массиви типи бутун бо андозаи N дода шудааст. Ҳамаи ададҳои тоқи массивро ба қимати ибтидоии адади тоқи охирин ҷамъ кунед. Агар дар массив адади тоқ набошад, пас массивро бе дигаргунӣ гузоред.`
	a.tj[68] = `Массив бо андозаи N дода шудааст. Ҷойҳои элементҳои калонтарин ва хурдтарини онро иваз кунед.`
	a.tj[69] = `Массив бо андозаи N (N - адади ҷуфт) дода шудааст. Ҷойҳои элементи якӯми онро бо дуюмаш, сеюмро - бо чорӯмаш ва ғ. иваз кунед.`
	a.tj[70] = `Массив бо андозаи N (N — адади ҷуфт) дода шудааст. Ҷойҳои нимаи аввал ва нимаи дуюми массивро иваз кунед.`
	a.tj[71] = `Массив бо андозаи N дода шудааст. Тартиби элементҳои онро ба баръакс иваз кунед.`
	a.tj[72] = `Массиви A бо андозаи N ва ададҳои бутуни K ва L (1 <= K < L <= N) дода шудаанд. Он элементҳои массивро, ки дар байни элементҳои A<sub>K</sub> ва A<sub>L</sub> дар якҷоягӣ бо ин элементҳо ҷойгиранд, бо тартиби баръакс ҷойгир кунед.`
	a.tj[73] = `Массиви A бо андозаи N ва ададҳои бутуни K ва L (1 <= K < L <= N) дода шудаанд. Он элементҳои массивро, ки дар байни элементҳои A<sub>K</sub> ва A<sub>L</sub> ба ғайр аз ин элементҳо ҷойгиранд, бо тартиби баръакс ҷойгир кунед.`
	a.tj[74] = `Массив бо андозаи N дода шудааст. Қиматҳои он элементҳои массивро, ки дар байни элементҳои калонтарин ва хурдтарини он ҷойгиранд, ба ғайр аз элементҳои калонтарин ва хурдтарин, ба нул иваз кунед.`
	a.tj[75] = `Массив бо андозаи N дода шудааст. Он элементҳои массивро, ки дар байни элементҳои калонтарин ва хурдтарин ҷойгиранд, ба ғайр аз элементҳои калонтарин ва хурдтарин, ба тартиби баръакс иваз кунед.`
	a.tj[76] = `Массив бо андозаи N дода шудааст. Ҳамаи элементҳои калонтарини локалиро (яъне ададҳоеро, ки аз ҳамсояҳои худ калон ҳастанд), ба нул иваз кунед.`
	a.tj[77] = `Массив бо андозаи N дода шудаанд. Ҳамаи элементҳои хурдтарини локалиро (яъне ададҳоеро, ки аз ҳамсояҳои худ хурд ҳастанд), ба квадрат бардоред.`
	a.tj[78] = `Массив бо андозаи N дода шудааст. Ҳар як элементи массивро ба қимати миёнаи ин элемент ва ҳамсояҳояш иваз кунед.`
	a.tj[79] = `Массиви андозаи N дода шудааст. Кӯчиши элементҳои массивро ба рост ба як мавқеъ иҷро кунед (дар ин ҳангом A[1] ба A[2] мекӯчад, A[2] ба A[3], ..., A[n-1] - ба A[n], қимати ибтидоии элементи охирин бошад, партофта мешавад). Элементи аввалини массиви бадастовардашударо ба 0(нул) баробар кунед.`
	a.tj[80] = `Массиви андозаи N дода шудааст. Кӯчиши элементҳои массивро ба чап ба як мавқеъ иҷро кунед (дар ин ҳангом A[n] ба A[n-1] мекӯчад, A[n-1] - ба A[n-2], ..., A[2] - ба A[1], қимати ибтидоии элементи аввалин бошад, партофта мешавад). Элементи охирини массиви бадастовардашударо ба 0(нул) баробар кунед.`
	a.tj[81] = `Массиви андозаи N ва адади бутуни K (1≤K&lt;N) дода шудаанд. Кӯчиши элементҳои массивро ба рост ба K мавқеъ иҷро кунед (дар ин ҳангом A[1] ба A[k+1] мекӯчад, A[2] ба A[k+2], ..., A[n-k] - ба A[n], қиматҳои ибтидоии K-то элементҳои охирин партофта мешаванд). K-то элементҳои аввалини массиви бадастовардашударо ба 0(нул) баробар кунед.`
	a.tj[82] = `Массиви андозаи N ва адади бутуни K (1≤K&lt;N) дода шудаанд. Кӯчиши элементҳои массивро ба чап ба K мавқеъ иҷро кунед (дар ин ҳангом A[n] ба A[n-k] мекӯчад, A[n-1] - ба A[n-k-1], ..., A[k+1] - ба A[1], қиматҳои ибтидоии K-то элементҳои аввалин партофта мешаванд). K-то элементҳои охирини массиви бадастовардашударо ба 0(нул) баробар кунед.`
	a.tj[83] = `Массиви андозаи N дода шудааст. Кӯчиши даврии элементҳои массивро ба рост ба як мавқеъ иҷро кунед (дар ин ҳангом A[1] ба A[2] мекӯчад, A[2] ба A[3], ..., A[n] - ба A[1] мекӯчад).`
	a.tj[84] = `Массиви андозаи N дода шудааст. Кӯчиши даврии элементҳои массивро ба чап ба як мавқеъ иҷро кунед (дар ин ҳангом A[n] ба A[n-1] мекӯчад, A[n-1] ба A[n-2], ..., A[1] - ба A[n] мекӯчад).`
	a.tj[85] = `Массиви A бо андозаи N ва адади бутуни K (1≤K≤4, K&lt;N) дода шудаанд. Кӯчиши даврии элементҳои массивро ба рост ба K мавҷеъ иҷро кунед (дар ин ҳангом A[1] ба A[k+1] мекӯчад, A[2] - ба A[k+2], ..., A[n] - ба A[k] мекӯчад). Истифодабарии массиви ёридиҳандаи дорои 4 элементҳо иҷозат дода мешавад.`
	a.tj[86] = `Массиви A бо андозаи N ва адади бутуни K (1≤K≤4, K&lt;N) дода шудаанд. Кӯчиши даврии элементҳои массивро ба чап ба K мавқеъ иҷро кунед (дар ин ҳангом A[n] ба A[n-k] мекӯчад, A[n-1] - ба A[n-k-1], ..., A[1] - ба A[n-k+1] мекӯчад).  Истифодабарии массиви ёридиҳандаи дорои 4 элементҳо иҷозат дода мешавад.`
	a.tj[87] = `Массиви андозаи N дода шудааст, ки ҳамаи элементҳои он, ба ғайр аз аввалин, аз рӯи афзуншавӣ ба тартиб гузошта шудаанд. Элементи аввалини массивро ба мавқеи нав кӯчонида, массивро батартибгузошташуда кунед.`
	a.tj[88] = `Массиви андозаи N дода шудааст, ки ҳамаи элементҳои он, ба ғайр аз охирин, аз рӯи афзуншавӣ ба тартиб гузошта шудаанд. Элементи охирини массивро ба мавқеи нав кӯчонида, массивро батартибгузошташуда кунед.`
	a.tj[89] = `Массиви андозаи N дода шудааст, ки ҳамаи элементҳои он, ба ғайр аз яктояш, аз рӯи камшавӣ ба тартиб гузошта шудаанд. Элементи вайронкунандаи қонуниятро ба мавқеи нав кӯчонида, массивро батартибгузошташуда кунед.`
	a.tj[90] = `Массиви андозаи N ва адади бутун K (1<=K<=N) дода шудаанд. Аз массив элементи дорои рақами тартибии K-ро нест кунед.`
	a.tj[91] = `Массиви андозаи N ва ададҳои бутуни K ва L (1<=K&lt;L<=N) дода шудаанд. Аз массив элементҳои дорои рақамҳои тартибии аз K то L-ро дар якҷоягӣ нест кунед ва андозаи массиви бадастовардашуда ва таркиби онро хориҷ кунед.`
	a.tj[92] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи ададҳои тоқро нест кунед ва андозаи массиви бадастовардашуда ва таркиби онро хориҷ кунед.`
	a.tj[93] = `Массиви типи бутуни андозаи N(>2) дода шудааст. Аз массив ҳамаи элементҳои дорои рақамҳои тартибии ҷуфтро (2, 4, ... ) нест кунед. Оператори шартиро истифода набаред.`
	a.tj[94] = `Массиви типи бутуни андозаи N(>2) дода шудааст. Аз массив ҳамаи элементҳои дорои рақамҳои тартибии тоқро (1, 3, ...) нест кунед. Оператори шартиро истифода набаред.`
	a.tj[95] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи элементҳои ҳамсояи якхеларо, ба ғайр аз элементҳои аввалини онҳо, нест кунед.`
	a.tj[96] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи элементҳои якхеларо, ба ғайр аз элементҳои аввалини онҳо, нест кунед.`
	a.tj[97] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи элементҳои якхеларо, ба ғайр аз элементҳои охирини онҳо, нест кунед.`
	a.tj[98] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи элементҳоеро, ки аз се маротиба кам вомехӯранд, нест кунед ва андозаи массиви бадастовардашуда ва таркиби онро хориҷ кунед.`
	a.tj[99] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи элементҳоеро, ки аз ду маротиба зиёд вомехӯранд, нест кунед ва андозаи массиви бадастовардашуда ва таркиби онро хориҷ кунед.`
	a.tj[100] = `Массиви типи бутуни андозаи N дода шудааст. Аз массив ҳамаи элементҳоеро, ки расо ду маротиба вомехӯранд, нест кунед ва андозаи массиви бадастовардашуда ва таркиби онро хориҷ кунед.`
	a.tj[101] = `Массиви андозаи N ва адади бутуни K (1<=K<=N) дода шудаанд. Пеш аз элементи массив, ки дорои рақами тартибии K аст, элементи навро бо қимати нулӣ гузоред.`
	a.tj[102] = `Массиви андозаи N ва адади бутуни K (1<=K<=N) дода шудаанд. Пас аз элементи массив, ки дорои рақами тартибии K аст, элементи навро бо қимати нулӣ гузоред.`
	a.tj[103] = `Массив бо андозаи N дода шудааст. Элементи дорои қимати нулиро пеш аз элементи хурдтарини массив ва пас аз элементи калонтарини массив гузоред.`
	a.tj[104] = `Массиви андозаи N ва ду ададҳои бутуни K ва M (1<=K<=N, 1<=M<=10) дода шудаанд. Пеш аз элементи массив, ки дорои рақами тартибии K аст, M-то элементҳои навро бо қиматҳои нулӣ гузоред.`
	a.tj[105] = `Массив бо андозаи N ва ду ададҳои бутуни K ва M (1<=K<=N, 1<=M<=10) дода шудаанд. Пас аз элементи массив, ки дорои рақами тартибии K аст, M-то элементҳои навро бо қиматҳои нулӣ гузоред.`
	a.tj[106] = `Массив бо андозаи N дода шудааст. Дар ин массив элементҳои дорои рақамҳои тартибии ҷуфтро (2, 4, ... ) дутогӣ кунед. Оператори шартиро истифода набаред.`
	a.tj[107] = `Массиви андозаи N дода шудааст. Дар ин массив мавқеъҳои ҳамаи элементҳои дорои рақамҳои тартибии тоқро (1, 3, ... ) сетогӣ кунед. Оператори шартиро истифода набаред.`
	a.tj[108] = `Массиви андозаи N дода шудааст. Пеш аз ҳар як элементи мусбии массив элементро бо қимати нулӣ гузоред.`
	a.tj[109] = `Массиви андозаи N дода шудааст. Пас аз ҳар як элементи манфии массив элементро бо қимати нулӣ гузоред.`
	a.tj[110] = `Массиви типи бутуни андозаи N дода шудааст. Дар он ҳамаи ададҳои ҷуфтро дутогӣ кунед.`
	a.tj[111] = `Массиви типи бутуни андозаи N дода шудааст. Дар он ҳамаи ададҳои тоқро сетогӣ кунед.`
	a.tj[112] = `Массиви A бо андозаи N(<=6) дода шудааст. Онро бо усули полоиши ивази оддӣ (полоиши "пузырька") аз рӯи афзуншавӣ ба тартиб гузоред: бо роҳи муқоисаи элементҳои ҳамсояи он (A[1] ва A[2], A[2] ва A[3] ва ғ.) ва ивазкунии ҷойҳои онҳо массивро дида бароед, агар элементи чапи ҷуфти ададҳо аз росташ калон бошад; амалҳои тасвиршударо N-1 маротиба такрор кунед. Барои санҷиши амалҳои иҷрошаванда таркиби массивро пас аз ҳар як дидабароӣ хориҷ кунед. Ба назар гиред, ки ҳангоми ҳар як дидабароӣ миқдори ҷуфтҳои ададҳои таҳлилшавандаро 1-то кам кардан лозим аст.`
	a.tj[113] = `Массиви A бо андозаи N(<=6) дода шудааст. Онро бо усули полоиши интихоби оддӣ аз рӯи афзуншавӣ ба тартиб гузоред: Элементи калонтарини массивро ёфта, ҷойи онро бо элементи охирин (N-ӯм) иваз кунед; Амалҳои тасвиршударо N-1 маротиби иҷро кунед, ҳар дафъа миқдори элементҳои таҳлилшавандаро 1-то кам кунед ва таркиби массивро хориҷ кунед.`
	a.tj[114] = `Массиви A бо андозаи N(≤6) дода шудааст. Онро бо усули полоиши гузоришҳои оддӣ аз рӯи афзуншавӣ ба тартиб гузоред: элементҳои A[1] ва A[2]-ро муқоиса кунед ва ҳангоми зарурият бо мақсади афзуншаванда ба тартиб гузоштани онҳо ҷойҳои онҳоро иваз кунед; сонӣ ба элементи A[3] муроҷиат кунед ва онро бо нигоҳдории тартибнокиаш ба қисми чапи (аллакай батартибгузошташудаи) массив кӯчонед; ин равандро барои элементҳои боқимонда такрор кунед ва таркиби массивро пас аз коркарди ҳар як элемент (аз 2-юм то N-ӯм) хориҷ кунед. Ҳангоми иҷрои амалҳои тасвиршуда истифодабарии услуби "барьера" (элементи навбатиро пеш аз коркардаш ба элементи иловагии массив A[0] сабт кардан) қулай аст.`
	a.tj[115] = `Массиви A бо андозаи N дода шудааст. Массиви мазкурро дигаргун накарда, рақамҳои тартибии элементҳои онро бо он тартибе хориҷ кунед, дар он элементҳои ба рақамҳои тартибии хориҷшуда мувофиқоянда пайдарпайии афзуншавандаро ташкил медиҳанд. Усули полоиши «пузырька»-ро истифода баред (ниг. ба масъалаи Array112), онро ба намуди зерин тағйир диҳед: массиви типи бутуни рақамҳои тартибии I-ро созед, онро бо ададҳои аз 1 то N пур кунед; бо роҳи муқоисаи ҷуфтҳои элементҳои массиви A бо рақамҳои I[1] ва I[2], I[2] ва I[3], ... массиви A-ро дида бароед ва ҷойҳои элементҳои мувофиқояндаи массиви I-ро иваз кунед, агар элементи чапи ҷуфти ададҳо аз росташ калон бошад. Ҳодисаи тасвиркардашудаи дидабароиро N–1 маротиба такрор карда, дар массиви I пайдарпайии талабкардашудаи рақамҳоро пайдо мекунем.`
	a.tj[116] = `Массиви типи бутуни A бо андозаи N дода шудааст. Гурӯҳи элементҳои якхелаи пайиҳамояндаро силсила меномем, миқдори ин элементҳоро бошад, дарозии силсила (дарозии силсила ба 1 баробар буда метавонад) меномем. Ду массивҳои нави типи бутуни андозаи якхелаи B ва C-ро тартиб дода, ба массиви B дарозиҳои ҳамаи силсилаҳои массиви ибтидоиро сабт мекунем, ба массиви C бошад - қиматҳои элементҳоеро, ки ин силсилаҳоро ташкил медиҳанд, сабт мекунем.`
	a.tj[117] = `Массиви типи бутуни андозаи N дода шудааст. Пеш аз ҳар як силсилаи он элементи дорои қимати нулиро гузоред.`
	a.tj[118] = `Массиви типи бутуни андозаи N дода шудааст. Пас аз ҳар як силсилаи он элементи дорои қимати нулиро гузоред.`
	a.tj[119] = `Массиви типи бутуни андозаи N дода шудааст. Ҳар як силсилаи онро ба як элемент зиёд карда, массивро тағйир диҳед.`
	a.tj[120] = `Массиви типи бутуни андозаи N дода шудааст. Аз ҳар як силсилаи он якто элементро кам карда, массивро тағйир диҳед.`
	a.tj[121] = `Адади бутуни K(>0) ва массиви типи бутуни андозаи N дода шудаанд. Дарозии силсилаи дорои рақами тартибии K-и массивро дуто карда, массивро тағйир диҳед. Агар силсилаҳо дар массив аз K кам бошанд, пас массивро бетағйирот хориҷ кунед.`
	a.tj[122] = `Адади бутуни K (> 1) ва массиви типи бутуни андозаи N дода шудаанд. Аз массив силсилаи дорои рақами тартибии K-ро нест кунед. Агар силсилаҳо дар массив аз K кам бошанд, пас массивро бетағйирот хориҷ кунед.`
	a.tj[123] = `Адади бутуни K (> 1) ва массиви типи бутуни андозаи N дода шудаанд. Ҷойҳои силсилаи аввалини массив ва силсилаи дорои рақами тартибии K-и массивро иваз кунед. Агар силсилаҳо дар массив аз K кам бошанд, пас массивро бетағйирот хориҷ кунед.`
	a.tj[124] = `Адади бутуни K (> 0) ва массиви типи бутуни андозаи N дода шудаанд. Ҷойҳои силсилаи охирини массив ва силсилаи дорои рақами тартибии K-и онро иваз кунед. Агар силсилаҳо дар массив аз K кам бошанд, пас массивро бетағйирот хориҷ кунед.`
	a.tj[125] = `Адади бутуни L (> 1) ва массиви типи бутуни андозаи N дода шудаанд. Ҳар як силсилаи массивро, ки дарозиаш аз L хурд аст, ба як элемент бо қимати нулӣ иваз кунед.`
	a.tj[126] = `Адади бутуни L (> 0) ва массиви типи бутуни андозаи N дода шудаанд. Ҳар як силсилаи массивро, ки дарозиаш ба L баробар аст, ба як элемент бо қимати нулӣ иваз кунед.`
	a.tj[127] = `Адади бутуни L (> 0) ва массиви типи бутуни андозаи N дода шудаанд. Ҳар як силсилаи массивро, ки дарозиаш аз L калон аст, ба як элемент бо қимати нулӣ иваз кунед.`
	a.tj[128] = `Массиви типи бутуни андозаи N дода шудааст. Массивро бо роҳи калон кардани силсилаи аввалини дорои дарозии калонтарин ба як элемент тағйир диҳед.`
	a.tj[129] = `Массиви типи бутуни андозаи N дода шудааст. Массивро бо роҳи калон кардани силсилаи охирини дорои дарозии калонтарин ба як элемент тағйир диҳед.`
	a.tj[130] = `Массиви типи бутуни андозаи N дода шудааст. Массивро бо роҳи калон кардани ҳамаи силсилаҳои дорои дарозии калонтарин ба як элемент тағйир диҳед.`
	a.tj[131] = `Маҷмӯи A аз N нуқтаҳо дар ҳамворӣ ва нуқтаи B (нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудаанд. Аз маҷмӯи A нуқтаи ба нуқтаи B наздиктаринро ёбед. Масофаи байни нуқтаҳо R бо координатаҳои (x<sub>1</sub>, y<sub>1</sub>) ва (x<sub>2</sub>, y<sub>2</sub>) аз рӯи формулаи зерин ҳисоб карда мешавад: R = sqrt((x<sub>2</sub> - x<sub>1</sub>)<sup>2</sup> + (y<sub>2</sub> - y<sub>1</sub>)<sup>2</sup>).`
	a.tj[132] = `Маҷмӯи A аз N нуқтаҳо (нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудааст. Аз байни ҳамаи нуқтаҳои ин маҷмӯъ, ки дар чоряки дуюм мехобанд, нуқтаеро ёбед, ки аз оғози координатаҳо дуртарин аст. Агар чунин нуқта набошад, пас нуқтаро бо координатаҳои нулӣ хориҷ кунед.`
	a.tj[133] = `Маҷмӯи A аз N нуқтаҳо (нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудааст. Аз байни ҳамаи нуқтаҳои ин маҷмӯъ, ки дар чоряки якӯм ё сеюм мехобанд, нуқтаеро ёбед, ки ба оғози координатаҳо наздиктарин аст. Агар чунин нуқта набошад, пас нуқтаро бо координатаҳои нулӣ хориҷ кунед.`
	a.tj[134] = `Маҷмӯи A аз N нуқтаҳо (нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудааст. Ҷуфти нуқтаҳои фарқкунандаро аз ин маҷмӯъ, ки масофаи байни онҳо калонтарин аст, ҳамчунин ин масофаро низ ёбед (нуқтаҳо бо ҳамон тартибе, ки ҳангоми дохилкунии маҷмӯи A ҷой дода шуда буданд, хориҷ карда мешаванд).`
	a.tj[135] = `Маҷмӯъҳои A ва B, ки мувофиқан аз N<sub>1</sub> ва N<sub>2</sub> нуқтаҳо таркиб ёфтаанд (нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд), дода шудаанд. Масофаи хурдтаринро байни нуқтаҳои ин маҷмӯъҳо ва худи нуқтаҳои дар ин масофа ҷойгиршударо ёбед (дар аввал нуқтаи маҷмӯи A хориҷ карда мешавад, сонӣ нуқтаи маҷмӯи B).`
	a.tj[136] = `Маҷмӯи A аз N нуқтаҳо (N > 2, нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудааст. Чунин нуқтаеро аз маҷмӯи мазкур ёбед, ки суммаи масофаҳо аз он то нуқтаҳои дигари маҷмӯъ хурдтарин аст ва худи ин суммаро низ ёбед.`
	a.tj[137] = `Маҷмӯи A аз N нуқтаҳо (N > 2, нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудааст. Периметри калонтарини секунҷаеро ёбед, ки қуллаҳои он ба нуқтаҳои гуногуни маҷмӯи A тааллуқ доранд ва худи ин нуқтаҳоро низ ёбед (нуқтаҳо бо ҳамон тартибе, ки ҳангоми дохилкунии маҷмӯи A ҷой дода шуда буданд, хориҷ карда мешаванд).`
	a.tj[138] = `Маҷмӯи A аз N нуқтаҳо (N > 2, нуқтаҳо бо координатаҳои x ва y-и худ дода шудаанд) дода шудааст. Периметри хурдтарини секунҷаеро ёбед, ки қуллаҳои он ба нуқтаҳои гуногуни маҷмӯи A тааллуқ доранд ва худи ин нуқтаҳоро низ ёбед (нуқтаҳо бо ҳамон тартибе, ки ҳангоми дохилкунии маҷмӯи A ҷой дода шуда буданд, хориҷ карда мешаванд).`
	a.tj[139] = `Маҷмӯи A аз N нуқтаҳо бо координатаҳои типи бутуни x, y дода шудааст. Тартибро дар ҳамвории координатавӣ чунин муайян мекунем: (x<sub>1</sub>, y<sub>1</sub>) < (x<sub>2</sub>, y<sub>2</sub>),    агар ё x<sub>1</sub> < x<sub>2</sub>, ё x<sub>1</sub> = x<sub>2</sub> ва y<sub>1</sub> < y<sub>2</sub> бошад. Нуқтаҳои ин маҷмӯъро аз рӯи афзуншавӣ дар мувофиқатӣ бо тартиби нишондодашуда ҷойгир кунед.`
	a.tj[140] = `Маҷмӯи A аз N нуқтаҳо бо координатаҳои типи бутуни x, y дода шудаанд. Тартибро дар ҳамвории координатавӣ чунин муайян мекунем: (x<sub>1</sub>, y<sub>1</sub>) < (x<sub>2</sub>, y<sub>2</sub>),  агар ё x<sub>1</sub> + y<sub>1</sub> < x<sub>2</sub> + y<sub>2</sub>, ё x<sub>1</sub> + y<sub>1</sub> = x<sub>2</sub> + y<sub>2</sub> ва x<sub>1</sub> < x<sub>2</sub> бошад. Нуқтаҳои ин маҷмӯъро аз рӯи камшавӣ дар мувофиқатӣ бо тартиби нишондодашуда ҷойгир кунед.`
}

func (a *aarray) makeRu() {
	a.ru = make([]string, a.count)
	a.ru[1] = `Дано целое число&nbsp;<i>N</i> (&gt;&nbsp;0). Сформировать и вывести целочисленный массив размера&nbsp;<i>N</i>, содержащий <i>N</i>&nbsp;первых положительных нечетных чисел: 1,&nbsp;3, 5,&nbsp;&#8230;&nbsp;.`
	a.ru[2] = `Дано целое число&nbsp;<i>N</i> (&gt;&nbsp;0). Сформировать и вывести целочисленный массив размера&nbsp;<i>N</i>, содержащий степени двойки от первой до&nbsp;<i>N</i>-й: 2,&nbsp;4, 8,&nbsp;16,&nbsp;&#8230;&nbsp;.`
	a.ru[3] = `Дано целое число&nbsp;<i>N</i> (&gt;&nbsp;1), а также первый член&nbsp;<i>A</i> и разность&nbsp;<i>D</i> <i>арифметической прогрессии</i>. Сформировать и вывести массив размера&nbsp;<i>N</i>, содержащий <i>N</i>&nbsp;первых членов данной прогрессии: <i>A</i>, &nbsp; &nbsp;<i>A</i>&nbsp;+&nbsp;<i>D</i>, &nbsp; &nbsp;<i>A</i>&nbsp;+&nbsp;2&#183;<i>D</i>, &nbsp; &nbsp;<i>A</i>&nbsp;+&nbsp;3&#183;<i>D</i>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.ru[4] = `Дано целое число&nbsp;<i>N</i> (&gt;&nbsp;1), а также первый член&nbsp;<i>A</i> и знаменатель&nbsp;<i>D</i> <i>геометрической прогрессии</i>. Сформировать и вывести массив размера&nbsp;<i>N</i>, содержащий <i>N</i>&nbsp;первых членов данной прогрессии: <i>A</i>, &nbsp; &nbsp;<i>A</i>&#183;<i>D</i>, &nbsp; &nbsp;<i>A</i>&#183;<i>D</i><sup>2</sup>, &nbsp; &nbsp;<i>A</i>&#183;<i>D</i><sup>3</sup>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.ru[5] = `Дано целое число&nbsp;<i>N</i> (&gt;&nbsp;2). Сформировать и вывести целочисленный массив размера&nbsp;<i>N</i>, содержащий <i>N</i>&nbsp;первых элементов последовательности <i>чисел Фибоначчи</i> <i>F</i><sub><i>K</i></sub>: <i>F</i><sub>1</sub>&nbsp;=&nbsp;1, &nbsp; &nbsp; &nbsp; &nbsp;<i>F</i><sub>2</sub>&nbsp;=&nbsp;1, &nbsp; &nbsp; &nbsp; &nbsp;<i>F</i><sub><i>K</i></sub>&nbsp;=&nbsp;<i>F</i><sub><i>K</i>&#8722;2</sub>&nbsp;+&nbsp;<i>F</i><sub><i>K</i>&#8722;1</sub>, &nbsp; &nbsp;<i>K</i>&nbsp;=&nbsp;3,&nbsp;4,&nbsp;&#8230;&nbsp;.`
	a.ru[6] = `Даны целые числа&nbsp;<i>N</i> (&gt;&nbsp;2), <i>A</i> и&nbsp;<i>B</i>. Сформировать и вывести целочисленный массив размера&nbsp;<i>N</i>, первый элемент которого равен&nbsp;<i>A</i>, второй равен&nbsp;<i>B</i>, а каждый последующий элемент равен сумме всех предыдущих.`
	a.ru[7] = `Дан массив размера&nbsp;<i>N</i>. Вывести его элементы в обратном порядке.`
	a.ru[8] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Вывести все содержащиеся в данном массиве нечетные числа в порядке возрастания их индексов, а также их количество&nbsp;<i>K</i>.`
	a.ru[9] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Вывести все содержащиеся в данном массиве четные числа в порядке убывания их индексов, а также их количество&nbsp;<i>K</i>.`
	a.ru[10] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Вывести вначале все содержащиеся в данном массиве четные числа в порядке возрастания их индексов, а затем &#8212; все нечетные числа в порядке убывания их индексов.`
	a.ru[11] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>). Вывести элементы массива с порядковыми номерами, кратными&nbsp;<i>K</i>: <i>A</i><sub><i>K</i></sub>, <i>A</i><sub>2&#183;<i>K</i></sub>, <i>A</i><sub>3&#183;<i>K</i></sub>,&nbsp;&#8230;&nbsp;. Условный оператор не использовать.`
	a.ru[12] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (<i>N</i> &#8212; четное число). Вывести его элементы с четными номерами в порядке возрастания номеров: <i>A</i><sub>2</sub>, <i>A</i><sub>4</sub>, <i>A</i><sub>6</sub>,&nbsp;&#8230;, <i>A</i><sub><i>N</i></sub>. Условный оператор не использовать.`
	a.ru[13] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (<i>N</i> &#8212; нечетное число). Вывести его элементы с нечетными номерами в порядке убывания номеров: <i>A</i><sub><i>N</i></sub>, <i>A</i><sub><i>N</i>&#8722;2</sub>, <i>A</i><sub><i>N</i>&#8722;4</sub>,&nbsp;&#8230;, <i>A</i><sub>1</sub>. Условный оператор не использовать.`
	a.ru[14] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Вывести вначале его элементы с четными номерами (в порядке возрастания номеров), а затем &#8212; элементы с нечетными номерами (также в порядке возрастания номеров): <i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub>6</sub>, &nbsp; &nbsp;&#8230;, &nbsp; &nbsp;<i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>5</sub>, &nbsp; &nbsp;&#8230;&nbsp;.Условный оператор не использовать.`
	a.ru[15] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Вывести вначале его элементы с нечетными номерами в порядке возрастания номеров, а затем &#8212; элементы с четными номерами в порядке убывания номеров: <i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>5</sub>, &nbsp; &nbsp;&#8230;, &nbsp; &nbsp;<i>A</i><sub>6</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub>2</sub>.Условный оператор не использовать.`
	a.ru[16] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Вывести его элементы в следующем порядке: <i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i></sub>, &nbsp; &nbsp;<i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;2</sub>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.ru[17] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Вывести его элементы в следующем порядке: <i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i></sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;2</sub>, &nbsp; &nbsp;<i>A</i><sub><i>N</i>&#8722;3</sub>, &nbsp; &nbsp;&#8230;&nbsp;.`
	a.ru[18] = `Дан массив&nbsp;<i>A</i> ненулевых целых чисел размера&nbsp;10. Вывести значение первого из тех его элементов&nbsp;<i>A</i><sub><i>K</i></sub>, которые удовлетворяют неравенству <i>A</i><sub><i>K</i></sub>&nbsp;&lt;&nbsp;<i>A</i><sub>10</sub>. Если таких элементов нет, то вывести&nbsp;0.`
	a.ru[19] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;10. Вывести порядковый номер последнего из тех его элементов&nbsp;<i>A</i><sub><i>K</i></sub>, которые удовлетворяют двойному неравенству <i>A</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>A</i><sub><i>K</i></sub>&nbsp;&lt;&nbsp;<i>A</i><sub>10</sub>. Если таких элементов нет, то вывести&nbsp;0.`
	a.ru[20] = `Дан массив размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Найти сумму элементов массива с номерами от&nbsp;<i>K</i> до&nbsp;<i>L</i> включительно.`
	a.ru[21] = `Дан массив размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Найти среднее арифметическое элементов массива с номерами от&nbsp;<i>K</i> до&nbsp;<i>L</i> включительно.`
	a.ru[22] = `Дан массив размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&lt;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Найти сумму всех элементов массива, кроме элементов с номерами от&nbsp;<i>K</i> до&nbsp;<i>L</i> включительно.`
	a.ru[23] = `Дан массив размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&lt;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Найти среднее арифметическое всех элементов массива, кроме элементов с номерами от&nbsp;<i>K</i> до&nbsp;<i>L</i> включительно.`
	a.ru[24] = `Дан целочисленный массив размера&nbsp;<i>N</i>, не содержащий одинаковых чисел. Проверить, образуют ли его элементы <i>арифметическую прогрессию</i> (см. задание Array3). Если образуют, то вывести разность прогрессии, если нет &#8212; вывести&nbsp;0.`
	a.ru[25] = `Дан массив ненулевых целых чисел размера&nbsp;<i>N</i>. Проверить, образуют ли его элементы <i>геометрическую прогрессию</i> (см. задание Array4). Если образуют, то вывести знаменатель прогрессии, если нет &#8212; вывести&nbsp;0.`
	a.ru[26] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Проверить, чередуются ли в нем четные и нечетные числа. Если чередуются, то вывести&nbsp;0, если нет, то вывести порядковый номер первого элемента, нарушающего закономерность.`
	a.ru[27] = `Дан массив ненулевых целых чисел размера&nbsp;<i>N</i>. Проверить, чередуются ли в нем положительные и отрицательные числа. Если чередуются, то вывести&nbsp;0, если нет, то вывести порядковый номер первого элемента, нарушающего закономерность.`
	a.ru[28] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Найти минимальный элемент из его элементов с четными номерами:&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>4</sub>, <i>A</i><sub>6</sub>,&nbsp;&#8230;&nbsp;.`
	a.ru[29] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Найти максимальный элемент из его элементов с нечетными номерами:&nbsp;<i>A</i><sub>1</sub>, <i>A</i><sub>3</sub>, <i>A</i><sub>5</sub>,&nbsp;&#8230;&nbsp;.`
	a.ru[30] = `Дан массив размера&nbsp;<i>N</i>. Найти номера тех элементов массива, которые больше своего правого соседа, и количество таких элементов. Найденные номера выводить в порядке их возрастания.`
	a.ru[31] = `Дан массив размера&nbsp;<i>N</i>. Найти номера тех элементов массива, которые больше своего левого соседа, и количество таких элементов. Найденные номера выводить в порядке их убывания.`
	a.ru[32] = `Дан массив размера&nbsp;<i>N</i>. Найти номер его первого локального минимума (<i>локальный минимум</i> &#8212; это элемент, который меньше любого из своих соседей).`
	a.ru[33] = `Дан массив размера&nbsp;<i>N</i>. Найти номер его последнего локального максимума (<i>локальный максимум</i> &#8212; это элемент, который больше любого из своих соседей).`
	a.ru[34] = `Дан массив размера&nbsp;<i>N</i>. Найти максимальный из его локальных минимумов (определение <i>локального минимума</i> дано в задании Array32).`
	a.ru[35] = `Дан массив размера&nbsp;<i>N</i>. Найти минимальный из его локальных максимумов (определение <i>локального максимума</i> дано в задании Array33).`
	a.ru[36] = `Дан массив размера&nbsp;<i>N</i>. Найти максимальный из его элементов, не являющихся ни локальным минимумом, ни локальным максимумом (определения <i>локального минимума</i> и <i>локального максимума</i> даны в заданиях Array32 и Array33). Если таких элементов в массиве нет, то вывести&nbsp;0 (как вещественное число).`
	a.ru[37] = `Дан массив размера&nbsp;<i>N</i>. Найти количество участков, на которых его элементы возрастают.`
	a.ru[38] = `Дан массив размера&nbsp;<i>N</i>. Найти количество участков, на которых его элементы убывают.`
	a.ru[39] = `Дан массив размера&nbsp;<i>N</i>. Найти количество его <i>промежутков монотонности</i> (т.&nbsp;е. участков, на которых его элементы возрастают или убывают).`
	a.ru[40] = `Дано число&nbsp;<i>R</i> и массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Найти элемент массива, который <i>наиболее близок</i> к числу&nbsp;<i>R</i> (т.&nbsp;е. такой элемент <i>A</i><sub><i>K</i></sub>, для которого величина |<i>A</i><sub><i>K</i></sub>&nbsp;&#8722;&nbsp;<i>R</i>| является минимальной).`
	a.ru[41] = `Дан массив размера&nbsp;<i>N</i>. Найти два соседних элемента, сумма которых максимальна, и вывести эти элементы в порядке возрастания их индексов.`
	a.ru[42] = `Дано число&nbsp;<i>R</i> и массив размера&nbsp;<i>N</i>. Найти два соседних элемента массива, сумма которых наиболее близка к числу&nbsp;<i>R</i>, и вывести эти элементы в порядке возрастания их индексов (определение наиболее близких чисел дано в задании Array40).`
	a.ru[43] = `Дан целочисленный массив размера&nbsp;<i>N</i>, все элементы которого упорядочены (по возрастанию или по убыванию). Найти количество различных элементов в данном массиве.`
	a.ru[44] = `Дан целочисленный массив размера&nbsp;<i>N</i>, содержащий ровно два одинаковых элемента. Найти номера одинаковых элементов и вывести эти номера в порядке возрастания.`
	a.ru[45] = `Дан массив размера&nbsp;<i>N</i>. Найти номера двух ближайших элементов из этого массива (т.&nbsp;е. элементов с наименьшим модулем разности) и вывести эти номера в порядке возрастания.`
	a.ru[46] = `Дано число&nbsp;<i>R</i> и массив размера&nbsp;<i>N</i>. Найти два различных элемента массива, сумма которых наиболее близка к числу&nbsp;<i>R</i>, и вывести эти элементы в порядке возрастания их индексов (определение наиболее близких чисел дано в задании Array40).`
	a.ru[47] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Найти количество различных элементов в данном массиве.`
	a.ru[48] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Найти максимальное количество его одинаковых элементов.`
	a.ru[49] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Если он является <i>перестановкой</i>, т.&nbsp;е. содержит все числа от&nbsp;1 до&nbsp;<i>N</i>, то вывести&nbsp;0; в противном случае вывести номер первого недопустимого элемента.`
	a.ru[50] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>, являющийся перестановкой (определение <i>перестановки</i> дано в задании Array49). Найти количество <i>инверсий</i> в данной перестановке, т.&nbsp;е. таких пар элементов&nbsp;<i>A</i><sub><i>I</i></sub> и&nbsp;<i>A</i><sub><i>J</i></sub>, в которых большее число находится слева от меньшего: <i>A</i><sub><i>I</i></sub>&nbsp;&gt;&nbsp;<i>A</i><sub><i>J</i></sub> при <i>I</i>&nbsp;&lt;&nbsp;<i>J</i>.`
	a.ru[51] = `Даны массивы&nbsp;<i>A</i> и&nbsp;<i>B</i> одинакового размера&nbsp;<i>N</i>. Поменять местами их содержимое и вывести вначале элементы преобразованного массива&nbsp;<i>A</i>,  а затем &#8212; элементы преобразованного массива&nbsp;<i>B</i>.`
	a.ru[52] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Сформировать новый массив&nbsp;<i>B</i> того же размера, элементы которого определяются следующим образом: <table align="center"><tr><td align=right><i>B</i><sub><i>K</i></sub><td align=center>&nbsp;=&nbsp;<td align=right>2&#183;<i>A</i><sub><i>K</i></sub>,<td align=left>если <i>A</i><sub><i>K</i></sub>&nbsp;&lt;&nbsp;5,           <tr><td align=right>&nbsp;<td align=center>&nbsp;<td align=right><i>A</i><sub><i>K</i></sub>/2<td align=left>в противном случае.</table></p>`
	a.ru[53] = `Даны два массива&nbsp;<i>A</i> и&nbsp;<i>B</i> одинакового размера&nbsp;<i>N</i>. Сформировать новый массив&nbsp;<i>C</i> того же размера, каждый элемент которого равен максимальному из элементов массивов&nbsp;<i>A</i> и&nbsp;<i>B</i> с тем же индексом.`
	a.ru[54] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Переписать в новый целочисленный массив&nbsp;<i>B</i> все четные числа из исходного массива (в том же порядке) и вывести размер полученного массива&nbsp;<i>B</i> и его содержимое.`
	a.ru[55] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (&#8804;&nbsp;15). Переписать в новый целочисленный массив&nbsp;<i>B</i> все элементы с нечетными порядковыми номерами (1, 3,&nbsp;&#8230;) и вывести размер полученного массива&nbsp;<i>B</i> и его содержимое. Условный оператор не использовать.`
	a.ru[56] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (&#8804;&nbsp;15). Переписать в новый целочисленный массив&nbsp;<i>B</i> все элементы с порядковыми номерами, кратными трем (3, 6,&nbsp;&#8230;), и вывести размер полученного массива&nbsp;<i>B</i> и его содержимое. Условный оператор не использовать.`
	a.ru[57] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Переписать в новый целочисленный массив&nbsp;<i>B</i> того же размера вначале все элементы исходного массива с четными номерами, а затем &#8212; с нечетными: <i>A</i><sub>2</sub>, &nbsp; &nbsp;<i>A</i><sub>4</sub>, &nbsp; &nbsp;<i>A</i><sub>6</sub>, &nbsp; &nbsp;&#8230;, &nbsp; &nbsp;<i>A</i><sub>1</sub>, &nbsp; &nbsp;<i>A</i><sub>3</sub>, &nbsp; &nbsp;<i>A</i><sub>5</sub>, &nbsp; &nbsp;&#8230;&nbsp;.Условный оператор не использовать.`
	a.ru[58] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Сформировать новый массив&nbsp;<i>B</i> того же размера по следующему правилу: элемент&nbsp;<i>B</i><sub><i>K</i></sub> равен сумме элементов массива&nbsp;<i>A</i> с номерами от&nbsp;1 до&nbsp;<i>K</i>.`
	a.ru[59] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Сформировать новый массив&nbsp;<i>B</i> того же размера по следующему правилу: элемент&nbsp;<i>B</i><sub><i>K</i></sub> равен среднему арифметическому элементов массива&nbsp;<i>A</i> с номерами от&nbsp;1 до&nbsp;<i>K</i>.`
	a.ru[60] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Сформировать новый массив&nbsp;<i>B</i> того же размера по следующему правилу: элемент&nbsp;<i>B</i><sub><i>K</i></sub> равен сумме элементов массива&nbsp;<i>A</i> с номерами от&nbsp;<i>K</i> до&nbsp;<i>N</i>.`
	a.ru[61] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Сформировать новый массив&nbsp;<i>B</i> того же размера по следующему правилу: элемент&nbsp;<i>B</i><sub><i>K</i></sub> равен среднему арифметическому элементов массива&nbsp;<i>A</i> с номерами от&nbsp;<i>K</i> до&nbsp;<i>N</i>.`
	a.ru[62] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Сформировать два новых массива&nbsp;<i>B</i> и&nbsp;<i>C</i>: в массив&nbsp;<i>B</i> записать все положительные элементы массива&nbsp;<i>A</i>, в массив&nbsp;<i>C</i> &#8212; все отрицательные (сохраняя исходный порядок следования элементов). Вывести вначале размер и содержимое массива&nbsp;<i>B</i>, а затем &#8212; размер и содержимое массива&nbsp;<i>C</i>.`
	a.ru[63] = `Даны два массива&nbsp;<i>A</i> и&nbsp;<i>B</i> размера&nbsp;5, элементы которых упорядочены по возрастанию. Объединить эти массивы так, чтобы результирующий массив&nbsp;<i>C</i> (размера&nbsp;10) остался упорядоченным по возрастанию.`
	a.ru[64] = `Даны три целочисленных массива&nbsp;<i>A</i>, <i>B</i> и&nbsp;<i>C</i> размера&nbsp;<i>N</i><sub><i>A</i></sub>, <i>N</i><sub><i>B</i></sub>, <i>N</i><sub><i>C</i></sub> соответственно, элементы которых упорядочены по убыванию. Объединить эти массивы так, чтобы результирующий целочисленный массив&nbsp;<i>D</i> (размера&nbsp;<i>N</i><sub><i>A</i></sub>&nbsp;+&nbsp;<i>N</i><sub><i>B</i></sub>&nbsp;+&nbsp;<i>N</i><sub><i>C</i></sub>) остался упорядоченным по убыванию.`
	a.ru[65] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>). Преобразовать массив, увеличив каждый его элемент на исходное значение элемента <i>A</i><sub><i>K</i></sub>.`
	a.ru[66] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Увеличить все четные числа, содержащиеся в массиве, на исходное значение первого четного числа. Если четные числа в массиве отсутствуют, то оставить массив без изменений.`
	a.ru[67] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Увеличить все нечетные числа, содержащиеся в массиве, на исходное значение последнего нечетного числа. Если нечетные числа в массиве отсутствуют, то оставить массив без изменений.`
	a.ru[68] = `Дан массив размера&nbsp;<i>N</i>. Поменять местами его минимальный и максимальный элементы.`
	a.ru[69] = `Дан массив размера&nbsp;<i>N</i> (<i>N</i> &#8212; четное число). Поменять местами его первый элемент со вторым, третий &#8212; с четвертым и т.&nbsp;д.`
	a.ru[70] = `Дан массив размера&nbsp;<i>N</i> (<i>N</i> &#8212; четное число). Поменять местами первую и вторую половины массива.`
	a.ru[71] = `Дан массив размера&nbsp;<i>N</i>. Поменять порядок его элементов на обратный.`
	a.ru[72] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Переставить в обратном порядке элементы массива, расположенные между элементами&nbsp;<i>A</i><sub><i>K</i></sub> и&nbsp;<i>A</i><sub><i>L</i></sub>, включая эти элементы.`
	a.ru[73] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Переставить в обратном порядке элементы массива, расположенные между элементами&nbsp;<i>A</i><sub><i>K</i></sub> и&nbsp;<i>A</i><sub><i>L</i></sub>, не включая эти элементы.`
	a.ru[74] = `Дан массив размера&nbsp;<i>N</i>. Обнулить элементы массива, расположенные между его минимальным и максимальным элементами (не включая минимальный и максимальный элементы).`
	a.ru[75] = `Дан массив размера&nbsp;<i>N</i>. Переставить в обратном порядке элементы массива, расположенные между его минимальным и максимальным элементами, включая минимальный и максимальный элементы.`
	a.ru[76] = `Дан массив размера&nbsp;<i>N</i>. Обнулить все его <i>локальные</i> <i>максимумы</i> (т.&nbsp;е. числа, большие своих соседей).`
	a.ru[77] = `Дан массив размера&nbsp;<i>N</i>. Возвести в квадрат все его <i>локальные</i> <i>минимумы</i> (т.&nbsp;е. числа, меньшие своих соседей).`
	a.ru[78] = `Дан массив размера&nbsp;<i>N</i>. Заменить каждый элемент массива на среднее арифметическое этого элемента и его соседей.`
	a.ru[79] = `Дан массив размера&nbsp;<i>N</i>. Осуществить <i>сдвиг</i> элементов массива вправо на одну позицию (при этом <i>A</i><sub>1</sub>&nbsp;перейдет в&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>2</sub> &#8212; в&nbsp;<i>A</i><sub>3</sub>,&nbsp;&#8230;, <i>A</i><sub><i>N</i>&#8722;1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i></sub>, a исходное значение последнего элемента будет потеряно). Первый элемент полученного массива положить равным&nbsp;0.`
	a.ru[80] = `Дан массив размера&nbsp;<i>N</i>. Осуществить <i>сдвиг</i> элементов массива влево на одну позицию (при этом <i>A</i><sub><i>N</i></sub>&nbsp;перейдет в&nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, <i>A</i><sub><i>N</i>&#8722;1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i>&#8722;2</sub>,&nbsp;&#8230;, <i>A</i><sub>2</sub> &#8212; в&nbsp;<i>A</i><sub>1</sub>, a исходное значение первого элемента будет потеряно). Последний элемент полученного массива положить равным&nbsp;0.`
	a.ru[81] = `Дан массив размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>N</i>). Осуществить <i>сдвиг</i> элементов массива вправо на <i>K</i>&nbsp;позиций (при этом <i>A</i><sub>1</sub>&nbsp;перейдет в&nbsp;<i>A</i><sub><i>K</i>+1</sub>, <i>A</i><sub>2</sub> &#8212; в&nbsp;<i>A</i><sub><i>K</i>+2</sub>,&nbsp;&#8230;, <i>A</i><sub><i>N</i>&#8722;<i>K</i></sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i></sub>, а исходное значение <i>K</i>&nbsp;последних элементов будет потеряно). Первые <i>K</i>&nbsp;элементов полученного массива положить равными&nbsp;0.`
	a.ru[82] = `Дан массив размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>N</i>). Осуществить <i>сдвиг</i> элементов массива влево на <i>K</i>&nbsp;позиций (при этом <i>A</i><sub><i>N</i></sub>&nbsp;перейдет в&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i></sub>, <i>A</i><sub><i>N</i>&#8722;1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i>&#8722;1</sub>,&nbsp;&#8230;, <i>A</i><sub><i>K</i>+1</sub> &#8212; в&nbsp;<i>A</i><sub>1</sub>, а исходное значение <i>K</i>&nbsp;первых элементов будет потеряно). Последние <i>K</i>&nbsp;элементов полученного массива положить равными&nbsp;0.`
	a.ru[83] = `Дан массив размера&nbsp;<i>N</i>. Осуществить <i>циклический сдвиг</i> элементов массива вправо на одну позицию (при этом <i>A</i><sub>1</sub>&nbsp;перейдет в&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>2</sub> &#8212; в&nbsp;<i>A</i><sub>3</sub>,&nbsp;&#8230;, <i>A</i><sub><i>N</i></sub> &#8212; в&nbsp;<i>A</i><sub>1</sub>).`
	a.ru[84] = `Дан массив размера&nbsp;<i>N</i>. Осуществить <i>циклический сдвиг</i> элементов массива влево на одну позицию (при этом <i>A</i><sub><i>N</i></sub>&nbsp;перейдет в&nbsp;<i>A</i><sub><i>N</i>&#8722;1</sub>, <i>A</i><sub><i>N</i>&#8722;1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i>&#8722;2</sub>,&nbsp;&#8230;, <i>A</i><sub>1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i></sub>).`
	a.ru[85] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;4, <i>K</i>&nbsp;&lt;&nbsp;<i>N</i>). Осуществить <i>циклический сдвиг</i> элементов массива вправо на <i>K</i>&nbsp;позиций (при этом <i>A</i><sub>1</sub>&nbsp;перейдет в&nbsp;<i>A</i><sub><i>K</i>+1</sub>, <i>A</i><sub>2</sub> &#8212; в&nbsp;<i>A</i><sub><i>K</i>+2</sub>,&nbsp;&#8230;, <i>A</i><sub><i>N</i></sub> &#8212; в&nbsp;<i>A</i><sub><i>K</i></sub>). Допускается использовать вспомогательный массив из&nbsp;4&nbsp;элементов.`
	a.ru[86] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;4, <i>K</i>&nbsp;&lt;&nbsp;<i>N</i>). Осуществить <i>циклический сдвиг</i> элементов массива влево на <i>K</i>&nbsp;позиций (при этом <i>A</i><sub><i>N</i></sub>&nbsp;перейдет в&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i></sub>, <i>A</i><sub><i>N</i>&#8722;1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i>&#8722;1</sub>,&nbsp;&#8230;, <i>A</i><sub>1</sub> &#8212; в&nbsp;<i>A</i><sub><i>N</i>&#8722;<i>K</i>+1</sub>). Допускается использовать вспомогательный массив из&nbsp;4&nbsp;элементов.`
	a.ru[87] = `Дан массив размера&nbsp;<i>N</i>, все элементы которого, кроме первого, упорядочены по возрастанию. Сделать массив упорядоченным, переместив первый элемент на новую позицию.`
	a.ru[88] = `Дан массив размера&nbsp;<i>N</i>, все элементы которого, кроме последнего, упорядочены по возрастанию. Сделать массив упорядоченным, переместив последний элемент на новую позицию.`
	a.ru[89] = `Дан массив размера&nbsp;<i>N</i>, все элементы которого, кроме одного, упорядочены по убыванию. Сделать массив упорядоченным, переместив элемент, нарушающий упорядоченность, на новую позицию.`
	a.ru[90] = `Дан массив размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>). Удалить из массива элемент с порядковым номером&nbsp;<i>K</i>.`
	a.ru[91] = `Дан массив размера&nbsp;<i>N</i> и целые числа&nbsp;<i>K</i> и&nbsp;<i>L</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&lt;&nbsp;<i>L</i>&nbsp;&#8804;&nbsp;<i>N</i>). Удалить из массива элементы с номерами от&nbsp;<i>K</i> до&nbsp;<i>L</i> включительно и вывести размер полученного массива и его содержимое.`
	a.ru[92] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все нечетные числа и вывести размер полученного массива и его содержимое.`
	a.ru[93] = `Дан целочисленный массив размера&nbsp;<i>N</i> (&gt;&nbsp;2). Удалить из массива все элементы с четными номерами (2,&nbsp;4,&nbsp;&#8230;). Условный оператор не использовать.`
	a.ru[94] = `Дан целочисленный массив размера&nbsp;<i>N</i> (&gt;&nbsp;2). Удалить из массива все элементы с нечетными номерами (1,&nbsp;3,&nbsp;&#8230;). Условный оператор не использовать.`
	a.ru[95] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все соседние одинаковые элементы, оставив их первые вхождения.`
	a.ru[96] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все одинаковые элементы, оставив их первые вхождения.`
	a.ru[97] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все одинаковые элементы, оставив их последние вхождения.`
	a.ru[98] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все элементы, встречающиеся менее трех раз, и вывести размер полученного массива и его содержимое.`
	a.ru[99] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все элементы, встречающиеся более двух раз, и вывести размер полученного массива и его содержимое.`
	a.ru[100] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива все элементы, встречающиеся ровно два раза, и вывести размер полученного массива и его содержимое.`
	a.ru[101] = `Дан массив размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>). Перед элементом массива с порядковым номером&nbsp;<i>K</i> вставить новый элемент с нулевым значением.`
	a.ru[102] = `Дан массив размера&nbsp;<i>N</i> и целое число&nbsp;<i>K</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>). После элемента массива с порядковым номером&nbsp;<i>K</i>  вставить новый элемент с нулевым значением.`
	a.ru[103] = `Дан массив размера&nbsp;<i>N</i>. Вставить элемент с нулевым значением перед минимальным и после максимального элемента массива.`
	a.ru[104] = `Дан массив размера&nbsp;<i>N</i> и два целых числа&nbsp;<i>K</i> и&nbsp;<i>M</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>, 1&nbsp;&#8804;&nbsp;<i>M</i>&nbsp;&#8804;&nbsp;10). Перед элементом массива с номером&nbsp;<i>K</i> вставить <i>M</i>&nbsp;новых элементов с нулевыми значениями.`
	a.ru[105] = `Дан массив размера&nbsp;<i>N</i> и два целых числа&nbsp;<i>K</i> и&nbsp;<i>M</i> (1&nbsp;&#8804;&nbsp;<i>K</i>&nbsp;&#8804;&nbsp;<i>N</i>, 1&nbsp;&#8804;&nbsp;<i>M</i>&nbsp;&#8804;&nbsp;10). После элемента массива с номером&nbsp;<i>K</i> вставить <i>M</i>&nbsp;новых элементов с нулевыми значениями.`
	a.ru[106] = `Дан массив размера&nbsp;<i>N</i>. Продублировать в нем элементы с четными номерами (2,&nbsp;4,&nbsp;&#8230;). Условный оператор в цикле не использовать.`
	a.ru[107] = `Дан массив размера&nbsp;<i>N</i>. Утроить в нем вхождения всех элементов с нечетными номерами (1,&nbsp;3,&nbsp;&#8230;). Условный оператор в цикле не использовать.`
	a.ru[108] = `Дан массив размера&nbsp;<i>N</i>. Перед каждым положительным элементом массива вставить элемент с нулевым значением.`
	a.ru[109] = `Дан массив размера&nbsp;<i>N</i>. После каждого отрицательного элемента массива вставить элемент с нулевым значением.`
	a.ru[110] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Продублировать в нем все четные числа.`
	a.ru[111] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Утроить в нем вхождения всех нечетных чисел.`
	a.ru[112] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (&#8804;&nbsp;6). Упорядочить его по возрастанию методом сортировки <i>простым обменом</i> (<i>&#171;пузырьковой&#187; сортировкой</i>): просматривать массив, сравнивая его соседние элементы (<i>A</i><sub>1</sub>&nbsp;и&nbsp;<i>A</i><sub>2</sub>, <i>A</i><sub>2</sub> и&nbsp;<i>A</i><sub>3</sub> и&nbsp;т.&nbsp;д.) и меняя их местами, если левый элемент пары больше правого; повторить описанные действия <i>N</i>&nbsp;&#8722;&nbsp;1&nbsp;раз. Для контроля за выполняемыми действиями выводить содержимое массива после каждого просмотра. Учесть, что при каждом просмотре количество анализируемых пар можно уменьшить на&nbsp;1.`
	a.ru[113] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (&#8804;&nbsp;6). Упорядочить его по возрастанию методом сортировки <i>простым выбором</i>: найти максимальный элемент массива и поменять его местами с последним (<i>N</i>-м) элементом; выполнить описанные действия <i>N</i>&nbsp;&#8722;&nbsp;1&nbsp;раз, каждый раз уменьшая на&nbsp;1 количество анализируемых элементов и выводя содержимое массива.`
	a.ru[114] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i> (&#8804;&nbsp;6). Упорядочить его по возрастанию методом сортировки <i>простыми вставками</i>: сравнить элементы&nbsp;<i>A</i><sub>1</sub> и&nbsp;<i>A</i><sub>2</sub> и, при необходимости меняя их местами, добиться того, чтобы они оказались упорядоченными по возрастанию; затем обратиться к элементу&nbsp;<i>A</i><sub>3</sub> и переместить его в левую (уже упорядоченную) часть массива, сохранив ее упорядоченность; повторить этот процесс для остальных элементов, выводя содержимое массива после обработки каждого элемента (от&nbsp;2-го до&nbsp;<i>N</i>-го). При выполнении описанных действий удобно использовать прием &#171;барьера&#187;, записывая очередной элемент перед его обработкой в дополнительный элемент массива&nbsp;<i>A</i><sub>0</sub>.`
	a.ru[115] = `Дан массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Не изменяя данный массив, вывести номера его элементов в том порядке, в котором соответствующие им элементы образуют возрастающую последовательность. Использовать метод <i>&#171;пузырьковой&#187; сортировки</i> (см. задание Array112), модифицировав его следующим образом: создать вспомогательный целочисленный <i>массив номеров</i>&nbsp;<i>I</i>, заполнив его числами от&nbsp;1 до&nbsp;<i>N</i>; просматривать массив&nbsp;<i>A</i>, сравнивая пары элементов массива&nbsp;<i>A</i> с номерами&nbsp;<i>I</i><sub>1</sub> и&nbsp;<i>I</i><sub>2</sub>, <i>I</i><sub>2</sub> и&nbsp;<i>I</i><sub>3</sub>,&nbsp;&#8230; и&nbsp;меняя местами соответствующие элементы массива&nbsp;<i>I</i>, если левый элемент пары больше правого. Повторив описанную процедуру просмотра <i>N</i>&nbsp;&#8722;&nbsp;1&nbsp;раз, получим в массиве&nbsp;<i>I</i> требуемую последовательность номеров.`
	a.ru[116] = `Дан целочисленный массив&nbsp;<i>A</i> размера&nbsp;<i>N</i>. Назовем <i>серией</i> группу подряд идущих одинаковых элементов, а <i>длиной серии</i> &#8212; количество этих элементов (длина серии может быть равна&nbsp;1). Сформировать два новых целочисленных массива&nbsp;<i>B</i> и&nbsp;<i>C</i> одинакового размера, записав в массив&nbsp;<i>B</i> длины всех серий исходного массива, а в массив&nbsp;<i>C</i> &#8212; значения элементов, образующих эти серии.`
	a.ru[117] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Вставить перед каждой его серией элемент с нулевым значением (определение серии дано в задании Array116).`
	a.ru[118] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Вставить после каждой его серии элемент с нулевым значением (определение серии дано в задании Array116).`
	a.ru[119] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Преобразовать массив, увеличив каждую его серию на один элемент (определение серии дано в задании Array116).`
	a.ru[120] = `Дан целочисленный массив размера&nbsp;<i>N</i>, содержащий по крайней мере одну серию, длина которой больше&nbsp;1. Преобразовать массив, уменьшив каждую его серию на один элемент (определение серии дано в задании Array116).`
	a.ru[121] = `Дано целое число&nbsp;<i>K</i> (&gt;&nbsp;0) и целочисленный массив размера&nbsp;<i>N</i>. Преобразовать массив, удвоив длину его серии с номером&nbsp;<i>K</i> (определение серии дано в задании Array116). Если серий в массиве меньше&nbsp;<i>K</i>, то вывести массив без изменений.`
	a.ru[122] = `Дано целое число&nbsp;<i>K</i> (&gt;&nbsp;1) и целочисленный массив размера&nbsp;<i>N</i>. Удалить из массива серию с номером&nbsp;<i>K</i> (определение серии дано в задании Array116). Если серий в массиве меньше&nbsp;<i>K</i>, то вывести массив без изменений.`
	a.ru[123] = `Дано целое число&nbsp;<i>K</i> (&gt;&nbsp;1) и целочисленный массив размера&nbsp;<i>N</i>. Поменять местами первую серию массива и его серию с номером&nbsp;<i>K</i> (определение серии дано в задании Array116). Если серий в массиве меньше&nbsp;<i>K</i>, то вывести массив без изменений.`
	a.ru[124] = `Дано целое число&nbsp;<i>K</i> (&gt;&nbsp;0) и целочисленный массив размера&nbsp;<i>N</i>. Поменять местами последнюю серию массива и его серию с номером&nbsp;<i>K</i> (определение серии дано в задании Array116). Если серий в массиве меньше&nbsp;<i>K</i>, то вывести массив без изменений.`
	a.ru[125] = `Дано целое число&nbsp;<i>L</i> (&gt;&nbsp;1) и целочисленный массив размера&nbsp;<i>N</i>. Заменить каждую серию массива, длина которой меньше&nbsp;<i>L</i>, на один элемент с нулевым значением (определение серии дано в задании Array116).`
	a.ru[126] = `Дано целое число&nbsp;<i>L</i> (&gt;&nbsp;0) и целочисленный массив размера&nbsp;<i>N</i>. Заменить каждую серию массива, длина которой равна&nbsp;<i>L</i>, на один элемент с нулевым значением (определение серии дано в задании Array116).`
	a.ru[127] = `Дано целое число&nbsp;<i>L</i> (&gt;&nbsp;0) и целочисленный массив размера&nbsp;<i>N</i>. Заменить каждую серию массива, длина которой больше&nbsp;<i>L</i>, на один элемент с нулевым значением (определение серии дано в задании Array116).`
	a.ru[128] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Преобразовать массив, увеличив его первую серию наибольшей длины на один элемент (определение серии дано в задании Array116).`
	a.ru[129] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Преобразовать массив, увеличив его последнюю серию наибольшей длины на один элемент (определение серии дано в задании Array116).`
	a.ru[130] = `Дан целочисленный массив размера&nbsp;<i>N</i>. Преобразовать массив, увеличив все его серии наибольшей длины на один элемент (определение серии дано в задании Array116).`
	a.ru[131] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек на плоскости и точка&nbsp;<i>B</i> (точки заданы своими координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>). Найти точку из множества&nbsp;<i>A</i>, наиболее близкую к точке&nbsp;<i>B</i>. <i>Расстояние</i>&nbsp;<i>R</i> между точками с координатами (<i>x</i><sub>1</sub>,&nbsp;<i>y</i><sub>1</sub>) и&nbsp;(<i>x</i><sub>2</sub>,&nbsp;<i>y</i><sub>2</sub>) вычисляется по формуле:<i>R</i>&nbsp;=&nbsp;((<i>x</i><sub>2</sub>&nbsp;&#8722;&nbsp;<i>x</i><sub>1</sub>)<sup>2</sup>&nbsp;+&nbsp;(<i>y</i><sub>2</sub>&nbsp;&#8722;&nbsp;<i>y</i><sub>1</sub>)<sup>2</sup>)<sup>1/2</sup>.`
	a.ru[132] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек (точки заданы своими координатами <i>x</i>,&nbsp;<i>y</i>). Среди всех точек этого множества, лежащих во второй четверти, найти точку, наиболее удаленную от начала координат. Если таких точек нет, то вывести точку с нулевыми координатами.`
	a.ru[133] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек (точки заданы своими координатами <i>x</i>,&nbsp;<i>y</i>). Среди всех точек этого множества, лежащих в первой или третьей четверти, найти точку, наиболее близкую к началу координат. Если таких точек нет, то вывести точку с нулевыми координатами.`
	a.ru[134] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек (точки заданы своими координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>). Найти пару точек этого множества с максимальным расстоянием между ними и само это расстояние (точки выводятся в том же порядке, в котором они перечислены при задании множества&nbsp;<i>A</i>).`
	a.ru[135] = `Даны множества&nbsp;<i>A</i> и&nbsp;<i>B</i>, состоящие соответственно из&nbsp;<i>N</i><sub>1</sub> и&nbsp;<i>N</i><sub>2</sub> точек (точки заданы своими координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>). Найти минимальное расстояние между точками этих множеств и сами точки, расположенные на этом расстоянии (вначале выводится точка из множества&nbsp;<i>A</i>, затем точка из множества&nbsp;<i>B</i>).`
	a.ru[136] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек (<i>N</i>&nbsp;&gt;&nbsp;2, точки заданы своими координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>). Найти такую точку из данного множества, сумма расстояний от которой до остальных его точек минимальна, и саму эту сумму.`
	a.ru[137] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек (<i>N</i>&nbsp;&gt;&nbsp;2, точки заданы своими координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>). Найти наибольший периметр треугольника, вершины которого принадлежат различным точкам множества&nbsp;<i>A</i>, и сами эти точки (точки выводятся в том же порядке, в котором они перечислены при задании множества&nbsp;<i>A</i>).`
	a.ru[138] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек (<i>N</i>&nbsp;&gt;&nbsp;2, точки заданы своими координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>). Найти наименьший периметр треугольника, вершины которого принадлежат различным точкам множества&nbsp;<i>A</i>, и сами эти точки (точки выводятся в том же порядке, в котором они перечислены при задании множества&nbsp;<i>A</i>).`
	a.ru[139] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек с целочисленными координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>. <i>Порядок</i> на координатной плоскости определим следующим образом: (<i>x</i><sub>1</sub>,&nbsp;<i>y</i><sub>1</sub>)&nbsp;&lt;&nbsp;(<i>x</i><sub>2</sub>,&nbsp;<i>y</i><sub>2</sub>), &nbsp; &nbsp;если либо <i>x</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>x</i><sub>2</sub>, либо <i>x</i><sub>1</sub>&nbsp;=&nbsp;<i>x</i><sub>2</sub> и <i>y</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>y</i><sub>2</sub>.Расположить точки данного множества по возрастанию в соответствии с указанным порядком.`
	a.ru[140] = `Дано множество&nbsp;<i>A</i> из <i>N</i>&nbsp;точек с целочисленными координатами&nbsp;<i>x</i>,&nbsp;<i>y</i>. <i>Порядок</i> на координатной плоскости определим следующим образом: (<i>x</i><sub>1</sub>,&nbsp;<i>y</i><sub>1</sub>)&nbsp;&lt;&nbsp;(<i>x</i><sub>2</sub>,&nbsp;<i>y</i><sub>2</sub>), &nbsp; &nbsp;если либо <i>x</i><sub>1</sub>&nbsp;+&nbsp;<i>y</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>x</i><sub>2</sub>&nbsp;+&nbsp;<i>y</i><sub>2</sub>, либо <i>x</i><sub>1</sub>&nbsp;+&nbsp;<i>y</i><sub>1</sub>&nbsp;=&nbsp;<i>x</i><sub>2</sub>&nbsp;+&nbsp;<i>y</i><sub>2</sub> и <i>x</i><sub>1</sub>&nbsp;&lt;&nbsp;<i>x</i><sub>2</sub>.Расположить точки данного множества по убыванию в соответствии с указанным порядком.`
}
