	f.en[40] = `Given a file of integers, replace each file component, whose order number is an even number, with two zero components.`
	f.en[41] = `Given a file of integers, replace each file component, whose value is a positive number, with three zero components.`
	f.en[42] = `Given two untyped files, exchange their contents.`
